import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';

import { AdminContext } from '../../App';

const Availableusers = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);
    const [getMessages, setGetMessages] = useState([]);

    const getAllMessages = async () => {
        try {
            const res = await fetch('/getallmessages');
            const data = await res.json();
            setGetMessages(data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getAllMessages();
    }, []);

    const deleteMessage = async (messageId) => {
        try {
            await fetch('/deletemessage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId }),
            });
            // Refresh messages after deleting
            getAllMessages();
        } catch (error) {
            console.log(error);
        }
    };

    const Loginbutton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            {/* Sidebar and navigation code */}
            {/* ... */}
            {/* Salecar table code */}
            <div className="salecartableDiv">
                <h1 className="heading">
                    <span>Available Users</span>
                </h1>
                <table className="salecartable">
                    <thead>
                        <tr>
                            <th>NAME</th>
                            <th>EMAIL</th>
                            <th>PHONE</th>
                            <th>MESSAGE</th>
                            <th>DELETE</th>
                        </tr>
                    </thead>
                    <tbody>
                        {getMessages.map((message) => (
                            <tr key={message._id}>
                                <td>{message.name}</td>
                                <td>{message.email}</td>
                                <td>{message.phone}</td>
                                <td>{message.message}</td>
                                <td>
                                    <button
                                        className="btn btn-danger"
                                        onClick={() => deleteMessage(message._id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default Availableusers;
