import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import ApexCharts from 'apexcharts';

import { AdminContext } from '../../App';

const Rentcarreports = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [incomeByDate, setIncomeByDate] = useState({});

    const getRentCarIncome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET'
            });

            const data = await res.json();

            setIncomeByDate(data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getRentCarIncome();
    }, []);

    useEffect(() => {
        if (Object.keys(incomeByDate).length > 0) {
            renderChart();
        }
    }, [incomeByDate]);

    const renderChart = () => {
        const labels = Object.keys(incomeByDate);
        const totalIncomeData = Object.values(incomeByDate).map(
            income => income.totalIncome
        );
        const soldItemsCountData = Object.values(incomeByDate).map(
            income => income.count
        );

        const options = {
            chart: {
                type: 'bar'
            },
            xaxis: {
                categories: labels,
                title: {
                    text: 'Date'
                }
            },
            yaxis: {
                title: {
                    text: 'Amount'
                }
            }
        };

        const series = [
            {
                name: 'Total Income',
                data: totalIncomeData
            },
            {
                name: 'Sold Items Count',
                data: soldItemsCountData
            }
        ];

        const chartOptions = {
            options,
            series,
            chart: {
                id: 'rentcar-chart'
            }
        };

        ApexCharts.exec('rentcar-chart', 'updateOptions', chartOptions);
    };

    const Loginbutton = () => {
        // Login button logic
    };

    return (
        <>
            <div className="sidebar">
                {/* Sidebar content */}
            </div>

            <section className="home-section">
                {/* Navigation and profile details */}
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Rented Cars Income Report</span>
                    </h1>

                    <div id="rentcar-chart" />

                    <div className="chart-container">
                        <div id="rentcar-chart" />
                    </div>
                </div>
            </section>
        </>
    );
};

export default Rentcarreports;
