import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from "react-router-dom";
import Swal from 'sweetalert2';
import { UserContext } from "../App";

const BookingRequest = () => {
    const [bookingData, setBookingData] = useState({ name: '', email: '', phone: '', car: '', pickupDate: '', returnDate: '' });
    const [carList, setCarList] = useState([]);
    const { state } = useContext(UserContext);
    const history = useHistory();

    useEffect(() => {

        const fetchCarList = async () => {
            try {
                const response = await fetch('/getAvailableRentCars');
                const data = await response.json();
                const carNames = data.map((car) => `${car.brand} ${car.model} ${car.year}`);
                setCarList(carNames);
            } catch (error) {
                console.error('Error fetching car list:', error);
            }
        };


        fetchCarList();
    }, []);

    const handleChange = (e) => {
        setBookingData({ ...bookingData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Perform booking request submission logic
        // ...

        // Reset form after submission
        setBookingData({ name: '', email: '', phone: '', car: '', pickupDate: '', returnDate: '' });
    };

    return (
        <div>
            <h1>Booking Request</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input type="text" name="name" value={bookingData.name} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Email:
                    <input type="email" name="email" value={bookingData.email} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Phone:
                    <input type="tel" name="phone" value={bookingData.phone} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Car:
                    <select name="car" value={bookingData.car} onChange={handleChange}>
                        <option value="">Select a car</option>
                        {carList.map((car, index) => (
                            <option key={index} value={car}>
                                {car}
                            </option>
                        ))}
                    </select>
                </label>
                <br />
                <label>
                    Pickup Date:
                    <input type="date" name="pickupDate" value={bookingData.pickupDate} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Return Date:
                    <input type="date" name="returnDate" value={bookingData.returnDate} onChange={handleChange} />
                </label>
                <br />
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default BookingRequest;
