import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import Swal from 'sweetalert2';
import { AdminContext } from '../../App';

const AdminBookingRequests = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);
    const [bookingRequests, setBookingRequests] = useState([]);

    const getBookingRequests = async () => {
        try {
            const res = await fetch('/getBookings');
            const data = await res.json();
            setBookingRequests(data.bookingRequests);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getBookingRequests();
    }, []);

    const updateStatus = async (bookingId, newStatus) => {
        try {
            await fetch(`/updateBookingStatus/${bookingId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ status: newStatus }),
            });
            getBookingRequests(); // Refresh booking requests after update
        } catch (error) {
            console.log(error);
        }
    };

    const handleStatusChange = (event, bookingId) => {
        const newStatus = event.target.value;
        updateStatus(bookingId, newStatus);
    };

    const LoginButton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            Logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            Login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <div className="sidebar">
                {/* Sidebar content */}
            </div>

            <section className="home-section">
                {/* Navigation and profile details */}

                <div className="booking-requests">
                    <h1>Booking Requests</h1>

                    <table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {bookingRequests.map((booking) => (
                                <tr key={booking._id}>
                                    <td>{booking.name}</td>
                                    <td>{booking.email}</td>
                                    <td>
                                        <select
                                            value={booking.status}
                                            onChange={(e) => handleStatusChange(e, booking._id)}
                                        >
                                            <option value="Pending">Pending</option>
                                            <option value="Approved">Approved</option>
                                            <option value="Rejected">Rejected</option>
                                        </select>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>
        </>
    );
};

export default AdminBookingRequests;
