import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useLocation, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const MoreDetails = () => {
    const { state, dispatch } = useContext(UserContext);
    const history = useHistory();

    let location = useLocation();
    const selectedCarId = location.state;
    const [userData, setUserData] = useState({
        id: '',
        name: '',
        email: '',
        message: '',
    });
    const [renttcarsData, setrenttcarsData] = useState({
        id: '',
        brand: '',
        model: '',
        year: '',
        color: '',
        seats: '',
        rent: '',
        fileName: '',
        filePath: '',
        fileType: '',
        fileSize: '',
        description: '',
    });
    const [allrenttcarReviews, setAllrenttcarReviews] = useState([]);
    const [days, setDays] = useState(1); // Number of days input

    const sendId = async () => {
        try {
            const res = await fetch('/sendReviewRentCarId', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    selectedCarId,
                }),
            });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        sendId();
    }, []);

    const reviewCarData = async () => {
        try {
            const res = await fetch('/getRentCarReviews', {
                method: 'GET',
            });

            const data = await res.json();
            setrenttcarsData({
                id: data.findCar._id,
                brand: data.findCar.brand,
                model: data.findCar.model,
                year: data.findCar.year,
                color: data.findCar.color,
                seats: data.findCar.seats,
                rent: data.findCar.rent,
                fileName: data.findCar.fileName,
                filePath: data.findCar.filePath,
                fileType: data.findCar.fileType,
                fileSize: data.findCar.fileSize,
                description: data.findCar.description,
            });

            setUserData({
                ...userData,
                id: data.findUser._id,
                name: data.findUser.name,
                email: data.findUser.email,
            });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        reviewCarData();
    }, []);

    const getallreviews = async () => {
        try {
            const res = await fetch('/getallreviewsforselectedrentcar', {
                method: 'GET',
            });

            const data = await res.json();

            setAllrenttcarReviews(data.allReviews);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getallreviews();
    }, []);

    const handleInputs = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setUserData({ ...userData, [name]: value });
    };

    const submitReviews = async (e) => {
        e.preventDefault();

        const { id, name, email, message } = userData;

        if (!name || !email || !message) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Message',
                text: 'Please fill in all the fields',
                showConfirmButton: true,
            });
            return;
        }

        const res = await fetch('/postrentcarreviews', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                id,
                name,
                email,
                message,
                selectedCarId,
            }),
        });

        const data = await res.json();

        if (data.status === 500 || !data) {
            Swal.fire({
                icon: 'error',
                title: 'Review not submitted',
                showConfirmButton: false,
                timer: 1500,
            });
            console.log('reviews not submitted');
        } else if (data.status === 201) {
            Swal.fire({
                icon: 'success',
                title: 'Review submitted',
                showConfirmButton: false,
                timer: 1500,
            });
            setUserData({ ...userData, message: '' });
            history.go(0); // Refresh the page
        } else {
            Swal.fire({
                icon: 'success',
                title: 'Review submitted',
                showConfirmButton: false,
                timer: 1500,
            });
            setUserData({ ...userData, message: '' });
            history.go(0); // Refresh the page
        }
    };

    const Loginbutton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    const addToCart = () => {
        const cartItem = {
            id: renttcarsData.id,
            brand: renttcarsData.brand,
            model: renttcarsData.model,
            year: renttcarsData.year,
            color: renttcarsData.color,
            seats: renttcarsData.seats,
            rent: renttcarsData.rent,
            fileName: renttcarsData.fileName,
            filePath: renttcarsData.filePath,
            fileType: renttcarsData.fileType,
            fileSize: renttcarsData.fileSize,
            description: renttcarsData.description,
            days: days,
        };

        dispatch({ type: 'ADD_TO_CART', payload: cartItem });

        Swal.fire({
            icon: 'success',
            title: 'Car added to cart',
            showConfirmButton: false,
            timer: 1500,
        });
    };

    const handleDaysChange = (e) => {
        setDays(e.target.value);
    };

    return (
        <>
            <header className="header">
                <div id="menu-btn" className="fas fa-bars"></div>
                <NavLink className="logo" to="/">
                    <span>Krily</span>Car{' '}
                </NavLink>
                <nav className="navbar">
                    <NavLink to="/">Home</NavLink>
                    <NavLink to="/rentcar">Rent A Car</NavLink>
                </nav>
                <div id="login-btn">
                    <Loginbutton />
                </div>
            </header>

            <div className="reviewsdiv" style={{ marginTop: '50px' }}>
                <h2>Car Details</h2>
                <img
                    src={renttcarsData.filePath}
                    alt=""
                    style={{ width: '50%', height: '50%' }}
                />
                <h4>
                    <b>{renttcarsData.brand}</b>
                </h4>
                <p>Model: {renttcarsData.model}</p>
                <p>Year: {renttcarsData.year}</p>
                <p>Color: {renttcarsData.color}</p>
                <p>Seats: {renttcarsData.seats}</p>
                <p>Rent: {renttcarsData.rent} DA</p>
                <p>Description: {renttcarsData.description}</p>

                <div className="theformdivrentcar">
                    <div className="daysinput">
                        <label htmlFor="days">Number of Days:</label>
                        <input
                            type="number"
                            id="days"
                            name="days"
                            min="1"
                            value={days}
                            onChange={handleDaysChange}
                        />
                    </div>
                    <div className="addtocartbutton">
                        <button className="btn" onClick={addToCart}>
                            Add to Cart
                        </button>
                    </div>
                </div>

                <div className="proceedtocart">
                    <NavLink className="btn" to="/cart">
                        Proceed to Cart
                    </NavLink>
                </div>
            </div>
        </>
    );
};

export default MoreDetails;
