import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';

import { UserContext } from '../App';

const Rentacar = () => {
    const { state, dispatch } = useContext(UserContext);
    const history = useHistory();

    const [rentCarsData, setRentCarsData] = useState([]);
    const [searchText, setSearchText] = useState('');
    const [searchYear, setSearchYear] = useState('');

    const allRentCars = async () => {
        try {
            if (!state) {
                Swal.fire({
                    title: 'Please sign in to rent a car!',
                    icon: 'warning',
                    showCancelButton: true,
                    confirmButtonText: 'OK',
                }).then((result) => {
                    if (result.isConfirmed) {
                        history.push('/signin');
                    } else {
                        history.push('/');
                    }
                });
            }

            const res = await fetch('/getRentCarData', {
                method: 'GET',
            });

            const data = await res.json();
            setRentCarsData(data);

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        allRentCars();
    }, []);

    const handleSearchText = (e) => {
        setSearchText(e.target.value);
    };

    const handleSearchYear = (e) => {
        setSearchYear(e.target.value);
    };

    const searchTextBtn = async () => {
        const res = await fetch('/searchRentCar', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                searchText,
                year: searchYear,
            }),
        });

        const data = await res.json();

        if (res.status === 200) {
            setRentCarsData(data);
        } else {
            console.log('Error occurred while searching');
        }
    };

    const Loginbutton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">
                            Sign In
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <div>
            <div>
                <div>
                    <h1>Rent a Car</h1>
                </div>

                <div>
                    <input
                        type="text"
                        placeholder="Search by brand or model"
                        value={searchText}
                        onChange={handleSearchText}
                    />

                    <input
                        type="number"
                        placeholder="Search by year"
                        value={searchYear}
                        onChange={handleSearchYear}
                    />

                    <button onClick={searchTextBtn}>Search</button>
                </div>

                <div>
                    {rentCarsData.map((rentCar) => (
                        <div key={rentCar._id}>
                            <h3>{rentCar.brand}</h3>
                            <p>{rentCar.model}</p>
                            <p>{rentCar.year}</p>
                            <p>{rentCar.rentperday}</p>
                            <p>{rentCar.fueltype}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Rentacar;
