import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from "react-router-dom";
import { UserContext } from "../App";

const Contact = () => {
    const [userData, setUserData] = useState({ name: "", email: "", phone: "", message: "" });
    const { state } = useContext(UserContext);
    const history = useHistory();
    const [showNotification, setShowNotification] = useState(false);

    const userContact = async () => {
        try {
            const res = await fetch('/getdata', {
                method: 'GET',
                headers: {
                    "Content-Type": "application/json"
                },
            });

            const data = await res.json();

            setUserData({ ...userData, name: data.name, email: data.email, phone: data.phone });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        userContact();
    }, []);

    const handleInputs = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setUserData({ ...userData, [name]: value });
    }

    const sendMessage = async (e) => {
        e.preventDefault();

        const { name, email, phone, message } = userData;

        const res = await fetch('/contact', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                name, email, phone, message
            })
        });

        const data = await res.json();

        if (!data) {
            console.log("message not sent");
        } else {
            alert("Message sent");
            setUserData({ ...userData, message: "" });
        }
    }

    const LoginButton = () => {
        if (state) {
            return (
                <div>
                    <button><NavLink className="btn" to="/signout">logout</NavLink></button>
                </div>
            );
        } else {
            return (
                <div>
                    <button onClick={() => setShowNotification(true)} className="btn">login</button>
                </div>
            );
        }
    };

    useEffect(() => {
        if (!state) {
            if (showNotification) {
                alert("Please sign in to access this page.");
            }
            history.push("/login"); // Redirect to login page if user is not logged in
        }
    }, [state, history, showNotification]);

    return (
        <>
            {state && (
                <div>
                    <div className="contactheader">
                        <header className="header">
                            <div id="menu-btn" className="fas fa-bars"></div>
                            <NavLink className="logo" to="/">
                                Car<span>Rent</span>
                            </NavLink>
                            <nav className="navbar">
                                <NavLink to="/">Home</NavLink>
                                <NavLink to="/rentcar">Rent A Car</NavLink>
                                <NavLink to="/about">About</NavLink>
                                <a href="/#services">Testimonial</a>
                                <a href="/contact">Contact</a>
                            </nav>
                            <div id="login-btn">
                                <LoginButton />
                            </div>
                        </header>
                    </div>
                    <section className="contact" id="contact">
                        <h1 className="heading">
                            <span>contact</span> us
                        </h1>
                        <div className="row">
                            <form method="POST">
                                <h3>Reach. Connect. Engage.</h3>
                                <input type="text" name="name" value={userData.name} onChange={handleInputs} placeholder="your name" className="box" />
                                <input type="email" name="email" value={userData.email} onChange={handleInputs} placeholder="your email" className="box" />
                                <input type="tel" name="phone" value={userData.phone} onChange={handleInputs} placeholder="your phone" className="box" />
                                <textarea placeholder="your message" name="message" value={userData.message} onChange={handleInputs} className="box" cols="30" rows="10"></textarea>
                                <input type="submit" value="send message" onClick={sendMessage} className="btn" />
                            </form>
                        </div>
                    </section>
                    <section className="footer" id="footer">

                        <div className="box-container">
                            {/* Footer content */}
                        </div>

                        <div className="credit">All rights reserved</div>
                    </section>
                </div>
            )}
        </>
    );
}

export default Contact;
