import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';

import { AdminContext } from '../../App';

const Rentcarreports = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [income, setIncome] = useState([]);
    const [carCount, setCarCount] = useState({});

    const getRentCarIncome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET',
            });

            const data = await res.json();

            setIncome(data);

            // Calculate the count for each car
            const count = {};
            data.forEach((item) => {
                item.soldItems.forEach((soldItem) => {
                    const { brand, model } = soldItem;
                    const carKey = `${brand}-${model}`;
                    count[carKey] = count[carKey] ? count[carKey] + 1 : 1;
                });
            });

            setCarCount(count);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getRentCarIncome();
    }, []);

    const Loginbutton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            {/* Sidebar */}
            {/* ... */}

            <section className="home-section">
                <nav>
                    {/* Navbar */}
                    {/* ... */}
                </nav>

                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Rented Cars Income Report</span>
                    </h1>

                    <table className="salecartable">
                        <thead>
                            <tr>
                                <th>BRAND</th>
                                <th>MODEL</th>
                                <th>BOOKED DAYS</th>
                                <th>INCOME</th>
                                <th>TOTAL INCOME</th> {/* Added column */}
                            </tr>
                        </thead>

                        <tbody>
                            {income.map((item) =>
                                item.soldItems.map((soldItem) => (
                                    <tr key={soldItem._id}>
                                        <td>{soldItem.brand}</td>
                                        <td>{soldItem.model}</td>
                                        <td>{soldItem.bookedDays}</td>
                                        <td>{soldItem.totalIncome} DA</td>
                                        <td>
                                            {carCount[`${soldItem.brand}-${soldItem.model}`]}
                                        </td> {/* Display total income */}
                                    </tr>
                                ))
                            )}
                        </tbody>
                    </table>
                </div>
            </section>
        </>
    );
};

export default Rentcarreports;
