import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const Contact = () => {
    const [userData, setUserData] = useState({ name: '', email: '', phone: '', message: '' });
    const { state } = useContext(UserContext);
    const history = useHistory();

    const handleInputs = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setUserData({ ...userData, [name]: value });
    };

    const sendMessage = async (e) => {
        e.preventDefault();

        const { name, email, phone, message } = userData;

        if (!state) {
            Swal.fire({
                title: 'Please signin to contact us!',
                icon: 'warning',
                confirmButtonText: 'OK'
            }).then((result) => {
                if (result.isConfirmed) {
                    history.push('/signin');
                }
            });
        } else {
            const res = await fetch('/contact', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name,
                    email,
                    phone,
                    message
                })
            });

            const data = await res.json();

            if (!data) {
                console.log('message not sent');
            } else {
                alert('Message sent');
                setUserData({ ...userData, message: '' });
            }
        }
    };



    const LoginButton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            {state && (
                <div>
                    <div className="contactheader">
                        <header className="header">
                            <div id="menu-btn" className="fas fa-bars"></div>
                            <NavLink className="logo" to="/">
                                Car<span>Rent</span>
                            </NavLink>
                            <nav className="navbar">
                                <NavLink to="/">Home</NavLink>
                                <NavLink to="/rentcar">Rent A Car</NavLink>
                                <NavLink to="/about">About</NavLink>
                                <a href="/#services">Testimonial</a>
                                <a href="/contact">Contact</a>
                            </nav>
                            <div id="login-btn">
                                <LoginButton />
                            </div>
                        </header>
                    </div>
                    <section className="contact" id="contact">
                        <h1 className="heading">
                            <span>contact</span> us
                        </h1>
                        <div className="row">
                            <form method="POST">
                                <h3>Reach. Connect. Engage.</h3>
                                <input
                                    type="text"
                                    name="name"
                                    value={userData.name}
                                    onChange={handleInputs}
                                    placeholder="your name"
                                    className="box"
                                />
                                <input
                                    type="email"
                                    name="email"
                                    value={userData.email}
                                    onChange={handleInputs}
                                    placeholder="your email"
                                    className="box"
                                />
                                <input
                                    type="tel"
                                    name="phone"
                                    value={userData.phone}
                                    onChange={handleInputs}
                                    placeholder="your phone"
                                    className="box"
                                />
                                <textarea
                                    placeholder="your message"
                                    name="message"
                                    value={userData.message}
                                    onChange={handleInputs}
                                    className="box"
                                    cols="30"
                                    rows="10"
                                ></textarea>
                                <input type="submit" value="send message" onClick={sendMessage} className="btn" />
                            </form>
                        </div>
                    </section>
                    <section className="footer" id="footer">
                        <div className="box-container">
                            <div className="box">
                                <h3>our branch</h3>
                                <a href="#">
                                    <i className="fas fa-map-marker-alt"></i> Algeria, Blida, Ouled Yaich
                                </a>
                            </div>
                            <div className="box">
                                <h3>quick links</h3>
                                <a href="#">
                                    <i className="fas fa-arrow-right"></i> home
                                </a>
                                <a href="rentcar">
                                    <i className="fas fa-arrow-right"></i> vehicles
                                </a>
                                <a href="#services">
                                    <i className="fas fa-arrow-right"></i> reviews
                                </a>
                                <a href="#contact">
                                    <i className="fas fa-arrow-right"></i> contact
                                </a>
                            </div>
                            <div className="box">
                                <h3>contact info</h3>
                                <a href="#">
                                    <i className="fas fa-phone"></i> +123-456-7890
                                </a>
                                <a href="#">
                                    <i className="fas fa-phone"></i> +111-222-3333
                                </a>
                                <a href="#">
                                    <i className="fas fa-envelope"></i> carrent@gmail.com
                                </a>
                            </div>
                            <div className="box">
                                <h3>contact info</h3>
                                <a href="#">
                                    <i className="fab fa-facebook-f"></i> facebook : carrentdz
                                </a>
                                <a href="#">
                                    <i className="fab fa-whatsapp"></i> whatsapp : +123-456-789
                                </a>
                                <a href="#">
                                    <i className="fab fa-instagram"></i> instagram : carrentdz
                                </a>
                            </div>
                        </div>
                        <div className="credit"> All rights reserved </div>
                    </section>
                </div>
            )}
        </>
    );
};

export default Contact;
