import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import frappe from 'frappe-charts';

import { AdminContext } from '../../App';

const Rentcarreports = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [incomeByDate, setIncomeByDate] = useState({});

    const getRentCarIncome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET'
            });

            const data = await res.json();

            setIncomeByDate(data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getRentCarIncome();
    }, []);

    useEffect(() => {
        if (Object.keys(incomeByDate).length > 0) {
            renderChart();
        }
    }, [incomeByDate]);

    const renderChart = () => {
        const labels = Object.keys(incomeByDate);
        const totalIncomeData = Object.values(incomeByDate).map(income => income.totalIncome);
        const soldItemsCountData = Object.values(incomeByDate).map(income => income.count);

        const chart = new frappe.Chart('#chart-container', {
            title: 'Rented Cars Income Report',
            data: {
                labels: labels,
                datasets: [
                    {
                        name: 'Total Income',
                        type: 'bar',
                        color: 'purple',
                        values: totalIncomeData
                    },
                    {
                        name: 'Sold Items Count',
                        type: 'bar',
                        color: 'blue',
                        values: soldItemsCountData
                    }
                ]
            },
            axisOptions: {
                xAxisMode: 'tick',
                yAxisMode: 'tick',
                xIsSeries: true
            }
        });
    };

    const Loginbutton = () => {
        // Login button logic
    };

    return (
        <>
            <div className="sidebar">
                <div className="logo-details">
                    <i className=''></i>
                    <span className='logo_name1'>Car</span><span className="logo_name">Rent</span>
                </div>
                <ul className="nav-links">
                    <li>
                        <NavLink className="dashlinks" to="/dashboard">
                            <i className='bx bx-grid-alt' ></i>
                            <span className="allLinks_name">Dashboard</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/addcars">
                            <i class="fa-sharp fa-solid fa-square-plus"></i>
                            <span className="allLinks_name">Add Cars</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/getrentcarsforadmin">
                            <i class="fa-sharp fa-solid fa-car"></i>
                            <span className="allLinks_name">Available Rent Cars</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/rentcarsreports">
                            <i class="fa-solid fa-sack-dollar"></i>
                            <span className="allLinks_name">Rent Cars Income</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/availableusers">
                            <i class="fa-solid fa-users"></i>
                            <span className="allLinks_name">Available Users</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/messages">
                            <i class="fa-solid fa-users"></i>
                            <span className="allLinks_name">Users Messages</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/requests">
                            <i class="fa-solid fa-envelope"></i>
                            <span className="allLinks_name">Booking Requests</span>
                        </NavLink>
                    </li>
                </ul>
                <div className="logoutbtnDashDiv">
                    <Loginbutton />
                </div>
            </div>

            <section className="home-section">
                {/* Navigation and profile details */}
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Rented Cars Income Report</span>
                    </h1>

                    <table className="salecartable">
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Total Income</th>
                                <th>Sold Items Count</th>
                            </tr>
                        </thead>

                        <tbody>
                            {Object.entries(incomeByDate).map(([date, income]) => (
                                <tr key={date}>
                                    <td>{date}</td>
                                    <td>{income.totalIncome} DA</td>
                                    <td>{income.count}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <div id="chart-container"></div>
                </div>
            </section>
        </>
    );
};

export default Rentcarreports;
