import React, { useState, useEffect, useContext, useRef } from 'react';
import { NavLink } from 'react-router-dom';

import { AdminContext } from '../../App';

const Statistics = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [income, setIncome] = useState([]);
    const [carCount, setCarCount] = useState({});
    const [totalIncomes, setTotalIncomes] = useState([]);
    const chartRef1 = useRef(null);
    const chartRef2 = useRef(null);

    const getRentCarIncome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET',
            });

            const data = await res.json();

            setIncome(data);

            // Calculate the count for each car
            const count = {};
            data.forEach((item) => {
                item.soldItems.forEach((soldItem) => {
                    const { brand, model } = soldItem;
                    const carKey = `${brand}-${model}`;
                    count[carKey] = count[carKey] ? count[carKey] + 1 : 1;
                });
            });

            setCarCount(count);

            // Calculate total incomes
            const incomes = data.map((item) =>
                item.soldItems.reduce((sum, soldItem) => sum + soldItem.income, 0)
            );
            setTotalIncomes(incomes);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getRentCarIncome();
    }, []);

    useEffect(() => {
        if (Object.keys(carCount).length > 0 && totalIncomes.length > 0) {
            // Create chart 1 - Repeat Count
            const labels1 = Object.keys(carCount);
            const values1 = Object.values(carCount);

            const chartConfig1 = {
                type: 'bar',
                data: {
                    labels: labels1,
                    datasets: [
                        {
                            label: 'Repeat Count',
                            data: values1,
                            backgroundColor: 'rgba(75, 192, 192, 0.6)',
                            borderWidth: 1,
                        },
                    ],
                },
                options: {
                    responsive: true,
                    scales: {
                        y: {
                            beginAtZero: true,
                        },
                    },
                },
            };

            const chart1 = new window.Chart(chartRef1.current, chartConfig1);

            // Create chart 2 - Total Incomes
            const labels2 = income.map((item, index) => `Sale ${index + 1}`);
            const values2 = totalIncomes;

            const chartConfig2 = {
                type: 'bar',
                data: {
                    labels: labels2,
                    datasets: [
                        {
                            label: 'Total Incomes',
                            data: values2,
                            backgroundColor: 'rgba(192, 75, 192, 0.6)',
                            borderWidth: 1,
                        },
                    ],
                },
                options: {
                    responsive: true,
                    scales: {
                        y: {
                            beginAtZero: true,
                        },
                    },
                },
            };

            const chart2 = new window.Chart(chartRef2.current, chartConfig2);

            // Cleanup
            return () => {
                chart1.destroy();
                chart2.destroy();
            };
        }
    }, [carCount, totalIncomes]);

    const Loginbutton = () => {
        // Login button logic
    };

    return (
        <>
            {/* Sidebar JSX */}
            <section className="home-section">
                {/* Header and navigation JSX */}
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Rented Cars Income Report</span>
                    </h1>

                    <table className="salecartable">
                        {/* Table content */}
                    </table>

                    {/* Chart 1 - Repeat Count */}
                    <div className="chartContainer">
                        <canvas ref={chartRef1}></canvas>
                    </div>

                    {/* Chart 2 - Total Incomes */}
                    <div className="chartContainer">
                        <canvas ref={chartRef2}></canvas>
                    </div>
                </div>
            </section>
        </>
    );
};

export default Statistics;
