import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const BookingRequest = () => {
    const [bookingData, setBookingData] = useState({
        name: '',
        email: '',
        phone: '',
        pickupDate: '',
        returnDate: '',
        car: ''
    });
    const [carList, setCarList] = useState([]);
    const { state } = useContext(UserContext);
    const history = useHistory();

    useEffect(() => {
        if (!state) {
            Swal.fire({
                title: 'Please sign in to make a booking request!',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'OK'
            }).then((result) => {
                if (result.isConfirmed) {
                    history.push('/signin');
                } else {
                    history.push('/');
                }
            });
        } else {
            fetchCarList();
        }
    }, [state, history]);

    const fetchCarList = async () => {
        try {
            const response = await fetch('/getAvailableRentCars');
            const data = await response.json();
            const carNames = data.map((car) => `${car.brand} ${car.model} ${car.year}`);
            setCarList(carNames);
        } catch (error) {
            console.error('Error fetching car list:', error);
        }
    };

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setBookingData({ ...bookingData, [name]: value });
    };

    const sendRequest = async (e) => {
        e.preventDefault();

        const { name, email, phone, pickupDate, returnDate, car } = bookingData;

        if (!name || !email || !phone || !pickupDate || !returnDate || !car) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Request',
                text: 'Please fill in all the fields',
                showConfirmButton: true,
            });
            return;
        }

        try {
            const res = await fetch('/saveBooking', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name,
                    email,
                    phone,
                    pickupDate,
                    returnDate,
                    car
                })
            });

            const data = await res.json();

            if (!data) {
                console.log('Request not sent');
            } else {
                Swal.fire({
                    icon: 'success',
                    title: 'Your request has been saved',
                    showConfirmButton: false,
                    timer: 1500
                });
                setBookingData({
                    name: '',
                    email: '',
                    phone: '',
                    pickupDate: '',
                    returnDate: '',
                    car: ''
                });
            }
        } catch (error) {
            console.log(error);
        }
    };

    const LoginButton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">Logout</NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">Login</NavLink>
                    </button>
                </div>
            );
        }
    };

    useEffect(() => {
        if (!state) {
            history.push('/signin'); // Redirect to login page if the user is not logged in
        }
    }, [state, history]);

    return (
        <>
            {state && (
                <div>
                    <header className="header">
                        <div id="menu-btn" className="fas fa-bars"></div>
                        <NavLink className="logo" to="/">
                            Car<span>Rent</span>
                        </NavLink>
                        <nav className="navbar">
                            <NavLink to="/">Home</NavLink>
                            <NavLink to="/rentcar">Rent A Car</NavLink>
                            <NavLink to="/about">About</NavLink>
                            <a href="/#services">Testimonial</a>
                            <a href="/contact">Contact</a>
                        </nav>
                        <div id="login-btn">
                            <LoginButton />
                        </div>
                    </header>
                    <div className="container">
                        <h1>Booking Request</h1>
                        <form onSubmit={sendRequest}>
                            <label>
                                Name:
                                <input
                                    type="text"
                                    name="name"
                                    value={bookingData.name}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Email:
                                <input
                                    type="email"
                                    name="email"
                                    value={bookingData.email}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Phone:
                                <input
                                    type="tel"
                                    name="phone"
                                    value={bookingData.phone}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Pickup Date:
                                <input
                                    type="date"
                                    name="pickupDate"
                                    value={bookingData.pickupDate}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Return Date:
                                <input
                                    type="date"
                                    name="returnDate"
                                    value={bookingData.returnDate}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Car:
                                <select
                                    name="car"
                                    value={bookingData.car}
                                    onChange={handleChange}
                                >
                                    <option value="">Select a car</option>
                                    {carList.map((car, index) => (
                                        <option key={index} value={car}>
                                            {car}
                                        </option>
                                    ))}
                                </select>
                            </label>
                            <button type="submit">Send Request</button>
                        </form>
                    </div>
                </div>
            )}
        </>
    );
};

export default BookingRequest;
