import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import Swal from 'sweetalert2';
import { AdminContext } from '../../App';

const Getrentcars = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);
    const [getCars, setGetCars] = useState([]);

    const getallrenttcars = async () => {
        try {
            const res = await fetch('/getAvailableRentCars', {
                method: 'GET',
            });

            const data = await res.json();
            setGetCars(data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getallrenttcars();
    }, []);

    const deleteUser = async (carIdFromDashBoard) => {
        try {
            await fetch('/deleteRentCarFromDashboard', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                }),
            });

            // Optional: Refresh the car list after deleting
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const setNewRentHandler = async (carIdFromDashBoard, newRent) => {
        if (!newRent) {
            // Show an error SweetAlert notification
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'Please enter a valid rent value',
            });
            return;
        }

        try {
            await fetch('/updateCarRent', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                    newRent,
                }),
            });

            // Show a success SweetAlert notification
            Swal.fire({
                icon: 'success',
                title: 'Rent Updated',
                text: 'The rent has been updated successfully!',
            });

            // Optional: Refresh the car list after updating the rent
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const setNewAvailabilityHandler = async (carIdFromDashBoard, newAvailability) => {
        if (!newAvailability) {
            // Show an error SweetAlert notification
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'Please enter a valid availability value',
            });
            return;
        }

        try {
            await fetch('/updateCarAvailability', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                    newAvailability,
                }),
            });

            // Show a success SweetAlert notification
            Swal.fire({
                icon: 'success',
                title: 'Availability Updated',
                text: 'The availability has been updated successfully!',
            });

            // Optional: Refresh the car list after updating the availability
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const resetAvailabilityHandler = async (carIdFromDashBoard) => {
        try {
            await fetch('/updateCarAvailability', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                    newAvailability: 'default', // Provide the default availability value
                }),
            });

            // Show a success SweetAlert notification
            Swal.fire({
                icon: 'success',
                title: 'Availability Reset',
                text: 'The availability has been reset successfully!',
            });

            // Optional: Refresh the car list after resetting the availability
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const Loginbutton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <div className="sidebar">
                <div className="logo-details">
                    <i className=""></i>
                    <span className="logo_name1">Krily</span>
                    <span className="logo_name">Car</span>
                </div>
                <ul className="nav-links">
                    <li>
                        <NavLink className="dashlinks" to="/dashboard">
                            <i className="bx bx-grid-alt"></i>
                            <span className="allLinks_name">Dashboard</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/addcars">
                            <i className="fa-sharp fa-solid fa-square-plus"></i>
                            <span className="allLinks_name">Add Cars</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/getrentcarsforadmin">
                            <i className="fa-sharp fa-solid fa-car"></i>
                            <span className="allLinks_name">Available Rent Cars</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/rentcarsreports">
                            <i className="fa-solid fa-sack-dollar"></i>
                            <span className="allLinks_name">Rent Cars Income</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/availableusers">
                            <i className="fa-solid fa-users"></i>
                            <span className="allLinks_name">Available Users</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/messages">
                            <i class="fa-solid fa-users"></i>
                            <span className="allLinks_name">Users Messages</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/requests">
                            <i class="fa-solid fa-envelope"></i>
                            <span className="allLinks_name">Booking Requests</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/st">
                            <i class="fa-solid fa-envelope"></i>
                            <span className="allLinks_name">Statistics</span>
                        </NavLink>
                    </li>
                </ul>

                <div className="logoutbtnDashDiv">
                    <Loginbutton />
                </div>
            </div>

            <section className="home-section">
                <nav>
                    <div className="sidebar-button">
                        <i className="fa fa-bars"></i>
                    </div>
                </nav>

                <div className="getrentcarsh2">
                    <h2>Rent Cars</h2>
                </div>

                <div className="table-wrapper-scroll-y my-custom-scrollbar">
                    <table className="table table-hover table-bordered">
                        <thead className="thead-dark">
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Car Name</th>
                                <th scope="col">Car Model</th>
                                <th scope="col">Car Rent Per Day</th>
                                <th scope="col">Availability</th>
                                <th scope="col">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {getCars.map((item, index) => (
                                <tr key={index}>
                                    <th scope="row">{index + 1}</th>
                                    <td>{item.carname}</td>
                                    <td>{item.carmodel}</td>
                                    <td>
                                        <input
                                            type="text"
                                            defaultValue={item.carrent}
                                            onChange={(e) => setNewRentHandler(item.carid, e.target.value)}
                                        />
                                    </td>
                                    <td>
                                        <input
                                            type="text"
                                            defaultValue={item.caravailability}
                                            onChange={(e) => setNewAvailabilityHandler(item.carid, e.target.value)}
                                        />
                                        <button
                                            className="resetAvailabilityBtn"
                                            onClick={() => resetAvailabilityHandler(item.carid)}
                                        >
                                            Reset
                                        </button>
                                    </td>
                                    <td>
                                        <button
                                            className="deleteBtn"
                                            onClick={() => deleteUser(item.carid)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>
        </>
    );
};

export default Getrentcars;
