import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const BookingRequest = () => {
    const [bookingData, setBookingData] = useState({
        name: '',
        email: '',
        phone: '',
        pickupDate: '',
        returnDate: '',
        car: ''
    });
    const [carList, setCarList] = useState([]);
    const { state } = useContext(UserContext);
    const history = useHistory();

    useEffect(() => {
        if (!state) {
            Swal.fire({
                title: 'Please sign in to make a booking request!',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'OK',
            }).then((result) => {
                if (result.isConfirmed) {
                    history.push('/signin');
                } else {
                    history.push('/');
                }
            });
        }
    }, [state, history]);

    useEffect(() => {
        const fetchCarList = async () => {
            try {
                const response = await fetch('/getAvailableRentCars');
                const data = await response.json();
                const carNames = data.map((car) => `${car.brand} ${car.model} ${car.year}`);
                setCarList(carNames);
            } catch (error) {
                console.error('Error fetching car list:', error);
            }
        };

        fetchCarList();
    }, []);

    useEffect(() => {
        if (state && state.user) {
            const { name, email, phone } = state.user;
            setBookingData({ ...bookingData, name, email, phone });
        }
    }, [state]);

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setBookingData({ ...bookingData, [name]: value });
    };

    const sendRequest = async (e) => {
        e.preventDefault();

        const { name, email, phone, pickupDate, returnDate, car } = bookingData;

        if (!name || !email || !phone || !pickupDate || !returnDate || !car) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Request',
                text: 'Please fill in all the fields',
                showConfirmButton: true,
            });
            return;
        }

        try {
            const res = await fetch('/saveBooking', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name,
                    email,
                    phone,
                    pickupDate,
                    returnDate,
                    car
                })
            });

            const data = await res.json();

            if (!data) {
                console.log('Request not sent');
            } else {
                Swal.fire({
                    icon: 'success',
                    title: 'Your request has been saved',
                    showConfirmButton: false,
                    timer: 1500
                });
                setBookingData({
                    name: '',
                    email: '',
                    phone: '',
                    pickupDate: '',
                    returnDate: '',
                    car: ''
                });
            }
        } catch (error) {
            console.log(error);
        }
    };

    const LoginButton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">logout</NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">login</NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <div className="contactheader">
                {/* Navigation code */}
            </div>
            <div className="booking-request">
                <form className='form-booking' method="POST">
                    <h1>Booking Request</h1>
                    <label>
                        Name:
                        <input type="text" name="name" value={bookingData.name} onChange={handleChange} placeholder="Your Name" required />
                    </label> <br />
                    <label>
                        Email:
                        <input type="email" name="email" value={bookingData.email} onChange={handleChange} placeholder="Your Email" required />
                    </label><br />
                    <label>
                        Phone:
                        <input type="tel" name="phone" value={bookingData.phone} onChange={handleChange} placeholder="Your Phone" required />
                    </label><br />
                    {/* Rest of the form code */}
                    <button type="submit" onClick={sendRequest}>Send Request</button>
                </form>
                {/* Footer section */}
            </div>
        </>
    );
};

export default BookingRequest;
