import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';


const BookingRequest = () => {
    const [bookingData, setBookingData] = useState({
        name: '',
        email: '',
        phone: '',
        pickupDate: '',
        returnDate: '',
        car: ''
    });
    const [carList, setCarList] = useState([]);
    const { state } = useContext(UserContext);
    const history = useHistory();
    useEffect(() => {
        if (!state) {
            Swal.fire({
                title: 'Please sign in to make a booking request!',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'OK',
            }).then((result) => {
                if (result.isConfirmed) {
                    history.push('/signin');
                } else {
                    history.push('/');
                }
            });
        }
    }, [state, history]);
    useEffect(() => {
        const fetchCarList = async () => {
            try {
                const response = await fetch('/getAvailableRentCars');
                const data = await response.json();
                const carNames = data.map((car) => `${car.brand} ${car.model} ${car.year}`);
                setCarList(carNames);
            } catch (error) {
                console.error('Error fetching car list:', error);
            }
        };

        fetchCarList();
    }, []);

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setBookingData({ ...bookingData, [name]: value });
    };

    const sendRequest = async (e) => {
        e.preventDefault();

        const { name, email, phone, pickupDate, returnDate, car } = bookingData;

        if (!name || !email || !phone || !pickupDate || !returnDate || !car) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Request',
                text: 'Please fill in all the fields',
                showConfirmButton: true,
            });
            return;
        }

        try {
            const res = await fetch('/saveBooking', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name,
                    email,
                    phone,
                    pickupDate,
                    returnDate,
                    car
                })
            });

            const data = await res.json();

            if (!data) {
                console.log('Request not sent');
            } else {
                Swal.fire({
                    icon: 'success',
                    title: 'Your request has been saved',
                    showConfirmButton: false,
                    timer: 1500
                });
                setBookingData({
                    name: '',
                    email: '',
                    phone: '',
                    pickupDate: '',
                    returnDate: '',
                    car: ''
                });
            }
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div className="booking-request">
            <h1>Booking Request</h1>
            <form>
                <label>
                    Name:
                    <input type="text" name="name" value={bookingData.name} onChange={handleChange} placeholder="Your Name" required />
                </label>
                <label>
                    Email:
                    <input type="email" name="email" value={bookingData.email} onChange={handleChange} placeholder="Your Email" required />
                </label>
                <label>
                    Phone:
                    <input type="tel" name="phone" value={bookingData.phone} onChange={handleChange} placeholder="Your Phone" required />
                </label>
                <label>
                    Pickup Date:
                    <input type="date" name="pickupDate" value={bookingData.pickupDate} onChange={handleChange} required />
                </label>
                <label>
                    Return Date:
                    <input type="date" name="returnDate" value={bookingData.returnDate} onChange={handleChange} required />
                </label>
                <label>
                    Car:
                    <select name="car" value={bookingData.car} onChange={handleChange}>
                        <option value="">Select a car</option>
                        {carList.map((car, index) => (
                            <option key={index} value={car}>
                                {car}
                            </option>
                        ))}
                    </select>
                </label>
                <button type="submit" onClick={sendRequest}>Send Request</button>
            </form>
        </div>
    );
};

export default BookingRequest;
