import React, { useState, useContext } from 'react';
import { NavLink, useHistory } from "react-router-dom";

import { AdminContext } from "../../App";

const Addcars = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const history = useHistory();
    const [file, setFile] = useState();
    const [car, setCar] = useState({
        brand: "",
        model: "",
        year: "",
        color: "",
        enginecc: "",
        maxpower: "",
        airbags: "",
        rearcamera: "",
        price: "",
        retailprice: "",
        quantity: "",
        description: "" // Added "description" field
    });

    let name, value;

    const handleInputs = (e) => {
        name = e.target.name;
        value = e.target.value;

        setCar({ ...car, [name]: value });
    };

    const handleFile = (e) => {
        const myfile = e.target.files[0];
        setFile({ ...car, myfile });
    };

    const postData = async (e) => {
        e.preventDefault();
        let formData = new FormData();
        formData.append('brand', file.brand);
        formData.append('model', file.model);
        formData.append('year', file.year);
        formData.append('color', file.color);
        formData.append('enginecc', file.enginecc);
        formData.append('maxpower', file.maxpower);
        formData.append('airbags', file.airbags);
        formData.append('rearcamera', file.rearcamera);
        formData.append('price', file.price);
        formData.append('retailprice', file.retailprice);
        formData.append('quantity', file.quantity);
        formData.append('description', file.description); // Added "description" field
        formData.append('myfile', file.myfile);

        const res = await fetch("/addcars", {
            method: "POST",
            body: formData
        });
    };

    return (
        <>
            <form method="POST" className="addcars_form" id="addcars_form">
                <div className="row mb-3">
                    <label htmlFor="brand" className="col-sm-2 col-form-label">Brand</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="brand" name="brand" value={car.brand} onChange={handleInputs} placeholder="Enter car brand" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="model" className="col-sm-2 col-form-label">Model</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="model" name="model" value={car.model} onChange={handleInputs} placeholder="Enter car model" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="year" className="col-sm-2 col-form-label">Year</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="year" name="year" value={car.year} onChange={handleInputs} placeholder="Enter car year" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="color" className="col-sm-2 col-form-label">Color</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="color" name="color" value={car.color} onChange={handleInputs} placeholder="Enter car color" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="enginecc" className="col-sm-2 col-form-label">Engine CC</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="enginecc" name="enginecc" value={car.enginecc} onChange={handleInputs} placeholder="Enter car engine CC" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="maxpower" className="col-sm-2 col-form-label">Max Power</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="maxpower" name="maxpower" value={car.maxpower} onChange={handleInputs} placeholder="Enter car max power" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="airbags" className="col-sm-2 col-form-label">Airbags</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="airbags" name="airbags" value={car.airbags} onChange={handleInputs} placeholder="Enter number of airbags" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="rearcamera" className="col-sm-2 col-form-label">Rear Camera</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="rearcamera" name="rearcamera" value={car.rearcamera} onChange={handleInputs} placeholder="Enter rear camera availability" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="price" className="col-sm-2 col-form-label">Price</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="price" name="price" value={car.price} onChange={handleInputs} placeholder="Enter car price" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="retailprice" className="col-sm-2 col-form-label">Retail Price</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="retailprice" name="retailprice" value={car.retailprice} onChange={handleInputs} placeholder="Enter car retail price" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="quantity" className="col-sm-2 col-form-label">Quantity</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="quantity" name="quantity" value={car.quantity} onChange={handleInputs} placeholder="Enter car quantity" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="description" className="col-sm-2 col-form-label">Description</label> // Added "description" field
                    <div className="col-sm-10">
                        <textarea className="form-control" id="description" name="description" value={car.description} onChange={handleInputs} placeholder="Enter car description" required></textarea> // Added "description" field
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="myfile" className="col-sm-2 col-form-label">File</label>
                    <div className="col-sm-10">
                        <input type="file" className="form-control" id="myfile" name="myfile" onChange={handleFile} required />
                    </div>
                </div>

                <div className="text-center">
                    <button type="submit" className="btn btn-primary" onClick={postData}>Add Car</button>
                    <NavLink to="/cars" className="btn btn-secondary mx-2">Back</NavLink>
                </div>
            </form>
        </>
    );
};

export default Addcars;
