import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../App';
const UserBookingRequests = () => {
    const { state } = useContext(UserContext);
    const [bookingRequests, setBookingRequests] = useState([]);

    useEffect(() => {
        const fetchBookingRequests = async () => {
            try {
                const response = await fetch('/getUserBookingRequests', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        userId: state.userId
                    })
                });
                const data = await response.json();
                setBookingRequests(data);
            } catch (error) {
                console.error('Error fetching booking requests:', error);
            }
        };

        if (state && state.userId) {
            fetchBookingRequests();
        }
    }, [state]);

    if (!state || !state.userId || bookingRequests.length === 0) {
        return null; // Return null if there are no booking requests or the user is not logged in
    }

    return (
        <div className="user-booking-requests">
            <h2>Your Booking Requests</h2>
            <ul>
                {bookingRequests.map((request) => (
                    <li key={request.id}>
                        <div>Name: {request.name}</div>
                        <div>Email: {request.email}</div>
                        <div>Phone: {request.phone}</div>
                        <div>Pickup Date: {request.pickupDate}</div>
                        <div>Return Date: {request.returnDate}</div>
                        <div>Car: {request.car}</div>
                    </li>
                ))}
            </ul>
        </div>
    );
};


export default UserBookingRequests;
