import React, { useState, useContext } from 'react';
import { NavLink, useHistory } from "react-router-dom";

import { AdminContext } from "../../App";

const Addcars = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);
    const history = useHistory();
    const [file, setFile] = useState();
    const [car, setCar] = useState({
        brand: "",
        model: "",
        year: "",
        color: "",
        enginecc: "",
        maxpower: "",
        airbags: "",
        rearcamera: "",
        price: "",
        retailprice: "",
        quantity: ""
    });
    const [message, setMessage] = useState(""); // State for success/error message

    const handleInputs = (e) => {
        setCar({ ...car, [e.target.name]: e.target.value });
    }

    const handleFile = (e) => {
        setFile(e.target.files[0]);
    }

    const postData = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('brand', car.brand);
        formData.append('model', car.model);
        // ... add other form fields to the formData object
        formData.append('myfile', file);

        try {
            const res = await fetch("/addcars", {
                method: "POST",
                body: formData
            });

            if (res.ok) {
                setMessage("Car added successfully!");
                setCar({ // Clear the form fields after successful submission
                    brand: "",
                    model: "",
                    year: "",
                    color: "",
                    enginecc: "",
                    maxpower: "",
                    airbags: "",
                    rearcamera: "",
                    price: "",
                    retailprice: "",
                    quantity: ""
                });
                setFile(null); // Clear the selected file
            } else {
                setMessage("Error adding car. Please try again.");
            }
        } catch (error) {
            setMessage("Error adding car. Please try again.");
        }
    }

    const Loginbutton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">logout</NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">login</NavLink>
                    </button>
                </div>
            );
        }
    }

    return (
        <>
            <div className="sidebar">
                {/* Sidebar content */}
            </div>

            <section className="home-section">
                <nav>
                    {/* Navigation content */}
                </nav>

                <div className="home-content">
                    <div className="sales-boxes">
                        <div className="recent-sales box">
                            <h1 className="heading"><span>Add Cars For Rent</span></h1>
                            <form method="POST" className="addcarform" name="rentform" id="myrentform">
                                {/* Form input fields */}
                                <div className="button">
                                    <input type="submit" name="submit" value="Submit" onClick={postData} />
                                </div>
                            </form>
                            {message && <p>{message}</p>} {/* Render the success/error message */}
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
}

export default Addcars;
