import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import Swal from 'sweetalert2';
import { AdminContext } from '../../App';

const AdminBookingRequests = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);
    const [bookingRequests, setBookingRequests] = useState([]);
    const [filteredBookingRequests, setFilteredBookingRequests] = useState([]);
    const [filterBy, setFilterBy] = useState('requestDay'); // Default filter option

    const getBookingRequests = async () => {
        try {
            const res = await fetch('/getBookings');
            const data = await res.json();
            setBookingRequests(data.bookings);
            setFilteredBookingRequests(data.bookings);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getBookingRequests();
    }, []);

    const handleFilterChange = (event) => {
        setFilterBy(event.target.value);

        if (event.target.value === 'requestDay') {
            setFilteredBookingRequests(bookingRequests);
        } else if (event.target.value === 'pickupDate') {
            const sortedByPickupDate = [...bookingRequests].sort((a, b) => {
                return new Date(a.pickupDate) - new Date(b.pickupDate);
            });
            setFilteredBookingRequests(sortedByPickupDate);
        } else if (event.target.value === 'car') {
            const sortedByCar = [...bookingRequests].sort((a, b) => {
                return a.car.localeCompare(b.car);
            });
            setFilteredBookingRequests(sortedByCar);
        }
    };

    const updateStatus = async (bookingId, newStatus) => {
        try {
            await fetch(`/updateBookingStatus/${bookingId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ status: newStatus }),
            });
            getBookingRequests(); // Refresh booking requests after update
        } catch (error) {
            console.log(error);
        }
    };

    const deleteBooking = async (bookingId) => {
        try {
            await fetch(`/deleteBooking/${bookingId}`, {
                method: 'DELETE',
            });
            getBookingRequests(); // Refresh booking requests after deletion
            Swal.fire('Success', 'Booking deleted successfully', 'success');
        } catch (error) {
            console.log(error);
        }
    };

    const handleStatusChange = (event, bookingId) => {
        const newStatus = event.target.value;
        updateStatus(bookingId, newStatus);
    };

    const LoginButton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            Logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            Login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <div className="sidebar">
                {/* Sidebar content */}
            </div>

            <section className="home-section">
                {/* Section content */}
                <div className="salecartableDivrq">
                    <h1 className="headingrq">
                        <span>Booking Requests</span>
                    </h1>

                    <div className="filter-container">
                        <label htmlFor="filterBy">Filter By:</label>
                        <select id="filterBy" value={filterBy} onChange={handleFilterChange}>
                            <option value="requestDay">Request Day</option>
                            <option value="pickupDate">Pickup Date</option>
                            <option value="car">Car</option>
                        </select>
                    </div>

                    <table className="salecartable">
                        <thead>
                            {/* Table header */}
                        </thead>

                        <tbody>
                            {filteredBookingRequests.map((booking) => (
                                <tr key={booking._id}>
                                    {/* Table row content */}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>
        </>
    );
};

export default AdminBookingRequests;
