import React, { useState, useEffect, useContext, useRef } from 'react';
import { NavLink } from 'react-router-dom';

import { AdminContext } from '../../App';

const Statistics = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [income, setIncome] = useState([]);
    const [carCount, setCarCount] = useState({});
    const [incomeChartData, setIncomeChartData] = useState({});
    const chartRef = useRef(null);

    const getrentcarincome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET',
            });

            const data = await res.json();

            setIncome(data);

            // Calculate the count for each car
            const count = {};
            data.forEach((item) => {
                item.soldItems.forEach((soldItem) => {
                    const { brand, model } = soldItem;
                    const carKey = `${brand}-${model}`;
                    count[carKey] = count[carKey] ? count[carKey] + 1 : 1;
                });
            });

            setCarCount(count);
        } catch (error) {
            console.log(error);
        }
    };

    const formatIncomeChartData = () => {
        const chartData = income.map((item) => {
            return item.soldItems.map((soldItem) => {
                return {
                    brand: soldItem.brand,
                    model: soldItem.model,
                    totalIncome: soldItem.totalIncome,
                };
            });
        });

        const flattenedData = [].concat(...chartData);

        const groupedData = flattenedData.reduce((acc, item) => {
            const { brand, model } = item;
            const carKey = `${brand}-${model}`;
            if (acc[carKey]) {
                acc[carKey].totalIncome += item.totalIncome;
            } else {
                acc[carKey] = {
                    brand,
                    model,
                    totalIncome: item.totalIncome,
                };
            }
            return acc;
        }, {});

        const chartValues = Object.values(groupedData);

        const sortedChartData = chartValues.sort((a, b) => b.totalIncome - a.totalIncome);

        return sortedChartData;
    };

    useEffect(() => {
        getrentcarincome();
    }, []);

    useEffect(() => {
        if (Object.keys(carCount).length > 0) {
            // Create chart for car count
            const countLabels = Object.keys(carCount);
            const countValues = Object.values(carCount);

            const countChartConfig = {
                type: 'bar',
                data: {
                    labels: countLabels,
                    datasets: [
                        {
                            label: 'Rented times',
                            data: countValues,
                            backgroundColor: 'rgba(228, 87, 46)',
                            borderWidth: 1,
                        },
                    ],
                },
                options: {
                    responsive: true,
                    scales: {
                        y: {
                            beginAtZero: true,
                        },
                    },
                    indexAxis: 'y',
                    barThickness: 'flex',
                },
            };

            const countChart = new window.Chart(chartRef.current, countChartConfig);

            return () => {
                countChart.destroy();
            };
        }
    }, [carCount]);

    useEffect(() => {
        const formattedData = formatIncomeChartData();
        setIncomeChartData(formattedData);
    }, [income]);

    const Loginbutton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <div className="sidebar">
                <div className="logo-details">
                    <i className=""></i>
                    <span className="logo_name1">Krily</span>
                    <span className="logo_name">Car</span>
                </div>
                <ul className="nav-links">
                    {/* Sidebar links */}
                </ul>

                <div className="logoutbtnDashDiv">
                    <Loginbutton />
                </div>
            </div>
            <section className="home-section">
                <nav>
                    <div className="sidebar-button">
                        <span className="dashboard">Dashboard</span>
                    </div>

                    <div className="profile-details">
                        <span className="admin_name">Admin</span>
                    </div>
                </nav>
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Most Rented Cars</span>
                    </h1>

                    {/* Bar chart */}
                    <div className="chartContainer">
                        <canvas ref={chartRef}></canvas>
                    </div>

                    <div className="totalIncomeTableDiv">
                        <h1 className="heading">
                            <span>Total Income by Car</span>
                        </h1>

                        {/* New Bar chart */}
                        {incomeChartData.length > 0 && (
                            <div className="chartContainer">
                                <canvas ref={chartRef}></canvas>
                            </div>
                        )}

                        <table className="totalIncomeTable">
                            {/* Table contents */}
                        </table>
                    </div>
                </div>
            </section>
        </>
    );
};

export default Statistics;
