import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import Chart from 'chart.js';

import { AdminContext } from '../../App';

const Rentcarreports = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [incomeByDate, setIncomeByDate] = useState({});

    const getRentCarIncome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET'
            });

            const data = await res.json();

            setIncomeByDate(data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getRentCarIncome();
    }, []);

    useEffect(() => {
        // Create the chart
        const ctx = document.getElementById('incomeChart').getContext('2d');

        const labels = Object.keys(incomeByDate);
        const totalIncomeData = Object.values(incomeByDate).map(
            income => income.totalIncome
        );
        const soldItemsCountData = Object.values(incomeByDate).map(
            income => income.count
        );

        new Chart(ctx, {
            type: 'bar',
            data: {
                labels: labels,
                datasets: [
                    {
                        label: 'Total Income',
                        data: totalIncomeData,
                        backgroundColor: 'rgba(54, 162, 235, 0.5)',
                        borderColor: 'rgba(54, 162, 235, 1)',
                        borderWidth: 1
                    },
                    {
                        label: 'Sold Items Count',
                        data: soldItemsCountData,
                        backgroundColor: 'rgba(255, 99, 132, 0.5)',
                        borderColor: 'rgba(255, 99, 132, 1)',
                        borderWidth: 1
                    }
                ]
            },
            options: {
                responsive: true,
                scales: {
                    x: {
                        display: true,
                        title: {
                            display: true,
                            text: 'Date'
                        }
                    },
                    y: {
                        display: true,
                        title: {
                            display: true,
                            text: 'Amount'
                        }
                    }
                }
            }
        });
    }, [incomeByDate]);

    const Loginbutton = () => {
        // Login button logic
    };

    return (
        <>
            <div className="sidebar">
                {/* Sidebar content */}
            </div>

            <section className="home-section">
                {/* Navigation and profile details */}
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Rented Cars Income Report</span>
                    </h1>

                    <div className="chart-container">
                        <canvas id="incomeChart" />
                    </div>
                </div>
            </section>
        </>
    );
};

export default Rentcarreports;
