import React, { useState, useEffect, useContext } from 'react'
import { NavLink } from "react-router-dom";

import { UserContext } from "../App"

const ExploreRentCar = () => {

    const { state, dispatch } = useContext(UserContext)


    const Loginbutton = () => {

        if (state) {
            return <div>
                <button className="btn"><NavLink className="nav-link" to="/signout">logout</NavLink></button>
            </div>
        }
        else {
            return <div>
                <button className="btn"><NavLink className="nav-link" to="/signin">login</NavLink></button>

            </div>
        }

    }



    const [renttcarsData, setrenttcarsData] = useState([]);

    const exploreRentCar = async () => {
        try {
            const res = await fetch('/exploreRentCarData', {
                method: 'GET',
            });

            const data = await res.json();

            setrenttcarsData(data)


            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }

        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        exploreRentCar();
    }, [])



    const alertDiv = document.getElementById("alertDiv")
    const handleClick = () => {
        if (alertDiv.style.display === "none") {
            alertDiv.style.display = "flex"
            window.alert("Please signin to rent the car!");
        }
        else {
            alertDiv.style.display = "flex"
        }
    }


    const hideAlert = () => {
        if (alertDiv.style.display === "flex") {
            alertDiv.style.display = "none"
        }
        else {
            alertDiv.style.display = "none"
        }
    }


    return (
        <>

            <header className="header">
                <div id="menu-btn" className="fas fa-bars"></div>
                <NavLink className="logo" to="/"> <span>Car</span>Rent </NavLink>

                <nav className="navbar">
                    <NavLink to="/">Home</NavLink>
                    <NavLink to="/rentcar">Rent A Car</NavLink>
                </nav>
                <div id="login-btn">
                    <Loginbutton />
                </div>
            </header>

            <div id="alertDiv" >
                <p> <b>Do You Like This Car?</b></p>
                <button className='btn' onClick={hideAlert}><NavLink to="/rentcar" className="nav-link">Rent Now</NavLink></button>
            </div>


            <div className="exploreCarsDiv">

                {renttcarsData.map((renttcarsData, index) =>

                    <div className="exploreCarsImg" key={renttcarsData._id}>

                        <img src={renttcarsData.filePath} alt="" style={{ width: "80%", height: "70%" }} onClick={handleClick} />
                        <h4><b>{renttcarsData.brand}</b></h4>
                        <p>{renttcarsData.model}</p>
                        <p>Year: {renttcarsData.year}</p>
                        <p>Price: {renttcarsData.price}</p>
                        <p>Seats: {renttcarsData.seats}</p>
                    </div>
                )}

            </div>
            <section className="footer" id="footer">

                <div className="box-container">

                    <div className="box">
                        <h3>our branch</h3>
                        <a href="#"> <i className="fas fa-map-marker-alt"></i> Algeria, Blida, Ouled Yaich </a>

                    </div>

                    <div className="box">
                        <h3>quick links</h3>
                        <a href="#"> <i className="fas fa-arrow-right"></i> home </a>
                        <a href="rentcar"> <i className="fas fa-arrow-right"></i> vehicles </a>
                        <a href="#services"> <i className="fas fa-arrow-right"></i> reviews </a>
                        <a href="#contact"> <i className="fas fa-arrow-right"></i> contact </a>
                    </div>

                    <div className="box">
                        <h3>contact info</h3>
                        <a href="#"> <i className="fas fa-phone"></i> +123-456-7890 </a>
                        <a href="#"> <i className="fas fa-phone"></i> +111-222-3333 </a>
                        <a href="#"> <i className="fas fa-envelope"></i> carrent@gmail.com </a>
                    </div>

                    <div className="box">
                        <h3>contact info</h3>
                        <a href="#"> <i className="fab fa-facebook-f"></i> facebook : carrentdz </a>
                        <a href="#"> <i className="fab fa-whatsapp"></i> whatsapp : +123-456-789</a>
                        <a href="#"> <i className="fab fa-instagram"></i> instagram : carrentdz</a>
                    </div>

                </div>


                <div className="credit">  Copyright &copy; {new Date().getFullYear()} Akrily. All rights reserved </div>

            </section>
        </>
    )
}

export default ExploreRentCar
