import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useLocation, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const MoreDetails = () => {
    const { state, dispatch } = useContext(UserContext);
    const history = useHistory();

    let location = useLocation();
    const selectedCarId = location.state;
    const [userData, setUserData] = useState({
        id: '',
        name: '',
        email: '',
        message: '',
        addToCart: 0, // Add to Cart input
        rentDays: 0, // Rent Days input
    });
    const [renttcarsData, setrenttcarsData] = useState({
        id: '',
        brand: '',
        model: '',
        year: '',
        color: '',
        seats: '',
        rent: '',
        fileName: '',
        filePath: '',
        fileType: '',
        fileSize: '',
        description: '',
    });
    const [allrenttcarReviews, setAllrenttcarReviews] = useState([]);

    const sendId = async () => {
        try {
            const res = await fetch('/sendReviewRentCarId', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    selectedCarId,
                }),
            });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        sendId();
    }, []);

    const reviewCarData = async () => {
        try {
            const res = await fetch('/getRentCarReviews', {
                method: 'GET',
            });

            const data = await res.json();
            setrenttcarsData({
                id: data.findCar._id,
                brand: data.findCar.brand,
                model: data.findCar.model,
                year: data.findCar.year,
                color: data.findCar.color,
                seats: data.findCar.seats,
                rent: data.findCar.rent,
                fileName: data.findCar.fileName,
                filePath: data.findCar.filePath,
                fileType: data.findCar.fileType,
                fileSize: data.findCar.fileSize,
                description: data.findCar.description,
            });

            setUserData({
                ...userData,
                id: data.findUser._id,
                name: data.findUser.name,
                email: data.findUser.email,
            });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };
    useEffect(() => {
        reviewCarData();
    }, []);

    const getallreviews = async () => {
        try {
            const res = await fetch('/getallreviewsforselectedrentcar', {
                method: 'GET',
            });

            const data = await res.json();

            setAllrenttcarReviews(data.allReviews);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getallreviews();
    }, []);

    const handleInputs = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setUserData({ ...userData, [name]: value });
    };

    const handleAddToCart = (e) => {
        const value = e.target.value;

        setUserData({ ...userData, addToCart: value });
    };

    const handleRentDays = (e) => {
        const value = e.target.value;

        setUserData({ ...userData, rentDays: value });
    };

    const submitReviews = async (e) => {
        e.preventDefault();

        const { id, name, email, message } = userData;

        // Rest of the code for submitting reviews
    };

    return (
        <div>
            <h1>Car Details</h1>
            <h2>{renttcarsData.brand}</h2>
            <h3>{renttcarsData.model}</h3>
            <h4>{renttcarsData.year}</h4>
            <h4>{renttcarsData.color}</h4>
            <h4>{renttcarsData.seats}</h4>
            <h4>{renttcarsData.rent}</h4>
            <img
                src={renttcarsData.filePath}
                alt={renttcarsData.fileName}
                width="200"
            />
            <p>{renttcarsData.description}</p>

            <div>
                <label htmlFor="addToCart">Add to Cart:</label>
                <input
                    type="number"
                    id="addToCart"
                    name="addToCart"
                    value={userData.addToCart}
                    onChange={handleAddToCart}
                />
            </div>

            <div>
                <label htmlFor="rentDays">Rent Days:</label>
                <input
                    type="number"
                    id="rentDays"
                    name="rentDays"
                    value={userData.rentDays}
                    onChange={handleRentDays}
                />
            </div>

            <form method="POST">
                <input
                    type="text"
                    name="name"
                    placeholder="Your Name"
                    value={userData.name}
                    onChange={handleInputs}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Your Email"
                    value={userData.email}
                    onChange={handleInputs}
                />
                <textarea
                    name="message"
                    placeholder="Your Review"
                    value={userData.message}
                    onChange={handleInputs}
                ></textarea>
                <button type="submit" onClick={submitReviews}>
                    Submit Review
                </button>
            </form>

            {/* Display existing reviews */}
            {allrenttcarReviews.map((review) => (
                <div key={review._id}>
                    <p>{review.name}</p>
                    <p>{review.message}</p>
                </div>
            ))}
        </div>
    );
};

export default MoreDetails;
