import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import Swal from 'sweetalert2';
import { AdminContext } from '../../App';

const Getrentcars = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);
    const [getCars, setGetCars] = useState([]);

    const getallrenttcars = async () => {
        try {
            const res = await fetch('/getAvailableRentCars', {
                method: 'GET',
            });

            const data = await res.json();
            const carsWithEditedPrice = data.map((car) => ({
                ...car,
                editedPrice: '',
                newRent: '', // Initialize new rent for each car
                newAvailability: '', // Initialize new availability for each car
            }));
            setGetCars(carsWithEditedPrice);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getallrenttcars();
    }, []);

    const deleteUser = async (e) => {
        const carIdFromDashBoard = e.target.id;

        try {
            await fetch('/deleteRentCarFromDashboard', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                }),
            });

            // Optional: Refresh the car list after deleting
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const setNewRentHandler = async (e, index) => {
        const carIdFromDashBoard = e.target.id;
        const newRent = getCars[index].newRent;

        if (!newRent) {
            // Show an error SweetAlert notification
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'Please enter a valid rent value',
            });
            return;
        }

        try {
            await fetch('/updateCarRent', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                    newRent,
                }),
            });

            // Show a success SweetAlert notification
            Swal.fire({
                icon: 'success',
                title: 'Rent Updated',
                text: 'The rent has been updated successfully!',
            });

            // Optional: Refresh the car list after updating the rent
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const setNewAvailabilityHandler = async (e, index) => {
        const carIdFromDashBoard = e.target.id;
        const newAvailability = getCars[index].newAvailability;

        if (!newAvailability) {
            // Show an error SweetAlert notification
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'Please enter a valid availability value',
            });
            return;
        }

        try {
            await fetch('/updateCarAvailability', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                    newAvailability,
                }),
            });

            // Show a success SweetAlert notification
            Swal.fire({
                icon: 'success',
                title: 'Availability Updated',
                text: 'The availability has been updated successfully!',
            });

            // Optional: Refresh the car list after updating the availability
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const Loginbutton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <div className="sidebar">
                <div className="logo-details">
                    <i className=""></i>
                    <span className="logo_name1">Krily</span>
                    <span className="logo_name">Car</span>
                </div>
                <ul className="nav-links">
                    <li>
                        <NavLink className="dashlinks" to="/dashboard">
                            <i className="bx bx-grid-alt"></i>
                            <span className="allLinks_name">Dashboard</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/addcars">
                            <i className="fa-sharp fa-solid fa-square-plus"></i>
                            <span className="allLinks_name">Add Cars</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/getrentcarsforadmin">
                            <i className="fa-sharp fa-solid fa-car"></i>
                            <span className="allLinks_name">Available Rent Cars</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/rentcarsreports">
                            <i className="fa-solid fa-sack-dollar"></i>
                            <span className="allLinks_name">Rent Cars Income</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/availableusers">
                            <i className="fa-solid fa-users"></i>
                            <span className="allLinks_name">Available Users</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/messages">
                            <i className="fa-solid fa-users"></i>
                            <span className="allLinks_name">Users Messages</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/requests">
                            <i className="fa-solid fa-envelope"></i>
                            <span className="allLinks_name">Booking Requests</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="dashlinks" to="/st">
                            <i className="fa-solid fa-envelope"></i>
                            <span className="allLinks_name">Statistics</span>
                        </NavLink>
                    </li>
                </ul>

                <div className="logoutbtnDashDiv">
                    <Loginbutton />
                </div>
            </div>

            <section className="home-section">
                <nav>
                    <div className="sidebar-button">
                        <span className="dashboard">Dashboard</span>
                    </div>

                    <div className="profile-details">
                        <span className="admin_name">Admin</span>
                    </div>
                </nav>

                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Available Rent Cars</span>
                    </h1>

                    <table className="salecartable">
                        <thead>
                            <tr>
                                <th>BRAND</th>
                                <th>MODEL</th>
                                <th>COLOR</th>
                                <th>YEAR</th>
                                <th>PRICE</th>
                                <th>AVAILABILITY</th>
                                <th>EDIT PRICE</th>
                                <th>EDIT AVAILABILITY</th>
                                <th>DELETE</th>
                            </tr>
                        </thead>

                        <tbody>
                            {getCars.map((val, index) => {
                                return (
                                    <tr key={val._id}>
                                        <td>{val.brand}</td>
                                        <td>{val.model}</td>
                                        <td>{val.color}</td>
                                        <td>{val.year}</td>
                                        <td>{val.price}</td>
                                        <td>{val.availability}</td>
                                        <td>
                                            <input
                                                type="text"
                                                placeholder="Enter new rent"
                                                id={val._id}
                                                value={val.newRent}
                                                onChange={(e) => {
                                                    const editedCars = [...getCars];
                                                    editedCars[index].newRent = e.target.value;
                                                    setGetCars(editedCars);
                                                }}
                                            />
                                            <button
                                                id={val._id}
                                                className="editbtn"
                                                onClick={(e) => setNewRentHandler(e, index)}
                                            >
                                                Save
                                            </button>
                                        </td>
                                        <td>
                                            <input
                                                type="text"
                                                placeholder="Enter new availability"
                                                id={val._id}
                                                value={val.newAvailability}
                                                onChange={(e) => {
                                                    const editedCars = [...getCars];
                                                    editedCars[index].newAvailability = e.target.value;
                                                    setGetCars(editedCars);
                                                }}
                                            />
                                            <button
                                                id={val._id}
                                                className="editbtn"
                                                onClick={(e) => setNewAvailabilityHandler(e, index)}
                                            >
                                                Save
                                            </button>
                                        </td>
                                        <td>
                                            <button
                                                id={val._id}
                                                className="deletebtn"
                                                onClick={(e) => deleteUser(e)}
                                            >
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
            </section>
        </>
    );
};

export default Getrentcars;
