import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';

import { UserContext } from '../App';

const Rentacar = () => {
    const { state, dispatch } = useContext(UserContext);
    const history = useHistory();
    const [rentCarsData, setRentCarsData] = useState([]);
    const [rentDays, setRentDays] = useState('');
    const [searchText, setSearchText] = useState('');

    const allRentCars = async () => {
        try {
            if (!state) {
                Swal.fire({
                    title: 'Please signin to rent a car!',
                    icon: 'warning',
                    showCancelButton: true,
                    confirmButtonText: 'OK'
                }).then((result) => {
                    if (result.isConfirmed) {
                        history.push('/signin');
                    } else {
                        history.push('/');
                    }
                });
            }

            const res = await fetch('/getRentCarData', {
                method: 'GET'
            });

            const data = await res.json();
            setRentCarsData(data);

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        allRentCars();
    }, []);

    const showDetails = (e) => {
        const currentCar = e.target.id;
        const specsDiv = document.getElementsByClassName('specsDivRentcar');
        const carDiv = document.getElementsByClassName('cardivRentcar');

        if (
            specsDiv[currentCar].style.display === 'none' &&
            carDiv[currentCar].style.display === 'block'
        ) {
            carDiv[currentCar].style.display = 'none';
            specsDiv[currentCar].style.display = 'block';
        } else {
            carDiv[currentCar].style.display = 'block';
            specsDiv[currentCar].style.display = 'none';
        }
    };

    const showCar = (e) => {
        const currentCar = e.target.id;
        const specsDiv = document.getElementsByClassName('specsDivRentcar');
        const carDiv = document.getElementsByClassName('cardivRentcar');

        if (
            specsDiv[currentCar].style.display === 'block' &&
            carDiv[currentCar].style.display === 'none'
        ) {
            specsDiv[currentCar].style.display = 'none';
            carDiv[currentCar].style.display = 'block';
        } else {
            specsDiv[currentCar].style.display = 'block';
            carDiv[currentCar].style.display = 'none';
        }
    };

    const addToCart = (e) => {
        const currentCar = e.target.id;
        const formDiv = document.getElementsByClassName('formDivRentcar');
        const specsDiv = document.getElementsByClassName('specsDivRentcar');
        const carDiv = document.getElementsByClassName('cardivRentcar');

        if (
            formDiv[currentCar].style.display === 'none' &&
            specsDiv[currentCar].style.display === 'none' &&
            carDiv[currentCar].style.display === 'block'
        ) {
            carDiv[currentCar].style.display = 'none';
            specsDiv[currentCar].style.display = 'none';
            formDiv[currentCar].style.display = 'block';
        } else {
            formDiv[currentCar].style.display = 'none';
            specsDiv[currentCar].style.display = 'none';
            carDiv[currentCar].style.display = 'block';
        }
    };

    const showCarAgain = (e) => {
        const currentCar = e.target.id;
        const formDiv = document.getElementsByClassName('formDivRentcar');
        const specsDiv = document.getElementsByClassName('specsDivRentcar');
        const carDiv = document.getElementsByClassName('cardivRentcar');

        if (
            formDiv[currentCar].style.display === 'none' &&
            specsDiv[currentCar].style.display === 'none' &&
            carDiv[currentCar].style.display === 'block'
        ) {
            formDiv[currentCar].style.display = 'block';
            specsDiv[currentCar].style.display = 'none';
            carDiv[currentCar].style.display = 'none';
        } else {
            formDiv[currentCar].style.display = 'block';
            specsDiv[currentCar].style.display = 'none';
            carDiv[currentCar].style.display = 'none';
        }
    };

    const handleDaysChange = (e) => {
        setRentDays(e.target.value);
    };

    const handleSearchTextChange = (e) => {
        setSearchText(e.target.value);
    };

    const getSearchData = async () => {
        try {
            const res = await fetch('/searchRentCars', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ searchText })
            });

            const data = await res.json();
            setRentCarsData(data);

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    const searchTextBtn = async () => {
        await getSearchData();
    };

    const proceedToCart = async (itemId) => {
        try {
            const res = await fetch('/addRentCarToCart', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    itemId: { _id: itemId },
                    rentDays
                })
            });

            const data = await res.json();
            if (res.status === 200) {
                Swal.fire('Success!', data.message, 'success');
            } else {
                Swal.fire('Error!', data.error, 'error');
            }

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div>
            <div className="searchBoxRentCar">
                <input
                    type="text"
                    placeholder="Search Cars by Name, Brand, or Model"
                    name="searchText"
                    value={searchText}
                    onChange={handleSearchTextChange}
                />
                <button onClick={searchTextBtn}>Search</button>
            </div>

            <div className="rentCar">
                {rentCarsData.map((car, index) => (
                    <div key={car._id}>
                        <div className="cardDivRentcar">
                            <img src={car.car_image} alt="" />
                            <h2>
                                {car.brand} {car.model}
                            </h2>
                            <h4>
                                Rent per day: <span>Rs. {car.rent_per_day}</span>
                            </h4>
                            <div>
                                <button id={index} onClick={showDetails}>
                                    Show Details
                                </button>
                                {state ? (
                                    <button id={index} onClick={addToCart}>
                                        Rent
                                    </button>
                                ) : (
                                    <NavLink to="/signin">
                                        <button>Signin to Rent</button>
                                    </NavLink>
                                )}
                            </div>
                        </div>

                        <div className="specsDivRentcar">
                            <h3>Car Specifications:</h3>
                            <ul>
                                <li>
                                    <span>Brand:</span> {car.brand}
                                </li>
                                <li>
                                    <span>Model:</span> {car.model}
                                </li>
                                <li>
                                    <span>Seating Capacity:</span> {car.seating_capacity}
                                </li>
                                <li>
                                    <span>Color:</span> {car.color}
                                </li>
                                <li>
                                    <span>Fuel Type:</span> {car.fuel_type}
                                </li>
                                <li>
                                    <span>Rent per day:</span> Rs. {car.rent_per_day}
                                </li>
                                <li>
                                    <span>Deposit Amount:</span> Rs. {car.deposit_amount}
                                </li>
                            </ul>
                            <div>
                                <button id={index} onClick={showCar}>
                                    Show Car
                                </button>
                                {state ? (
                                    <button id={index} onClick={addToCart}>
                                        Rent
                                    </button>
                                ) : (
                                    <NavLink to="/signin">
                                        <button>Signin to Rent</button>
                                    </NavLink>
                                )}
                            </div>
                        </div>

                        <div className="formDivRentcar">
                            <h3>Enter Rental Duration:</h3>
                            <input
                                type="number"
                                placeholder="No. of Days"
                                name="rentDays"
                                value={rentDays}
                                onChange={handleDaysChange}
                            />
                            <button
                                onClick={() => proceedToCart(car._id)}
                                id={index}
                                className="rentBtnRentcar"
                            >
                                Rent
                            </button>
                            <button id={index} onClick={showCarAgain} className="backBtnRentcar">
                                Back
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Rentacar;
