import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import Swal from 'sweetalert2';
import { AdminContext } from '../../App';

const AdminBookingRequests = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);
    const [bookingRequests, setBookingRequests] = useState([]);
    const [filterBy, setFilterBy] = useState('requestDay');
    const [filterValue, setFilterValue] = useState('');
    const [filteredBookings, setFilteredBookings] = useState([]);

    const getBookingRequests = async () => {
        try {
            const res = await fetch('/getBookings');
            const data = await res.json();
            setBookingRequests(data.bookings);

            // Apply filter
            let filteredData = data.bookings;
            if (filterBy === 'requestDay') {
                filteredData = filteredData.filter(booking =>
                    booking.createdAt.includes(filterValue)
                );
            } else if (filterBy === 'pickupDate') {
                filteredData = filteredData.filter(booking =>
                    booking.pickupDate.includes(filterValue)
                );
            } else if (filterBy === 'car') {
                filteredData = filteredData.filter(booking =>
                    booking.car.includes(filterValue)
                );
            }
            setFilteredBookings(filteredData);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getBookingRequests();
    }, []);

    const updateStatus = async (bookingId, newStatus) => {
        try {
            await fetch(`/updateBookingStatus/${bookingId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ status: newStatus }),
            });
            getBookingRequests(); // Refresh booking requests after update
        } catch (error) {
            console.log(error);
        }
    };

    const deleteBooking = async (bookingId) => {
        try {
            await fetch(`/deleteBooking/${bookingId}`, {
                method: 'DELETE',
            });
            getBookingRequests(); // Refresh booking requests after deletion
            Swal.fire('Success', 'Booking deleted successfully', 'success');
        } catch (error) {
            console.log(error);
        }
    };

    const handleFilterChange = (event) => {
        setFilterBy(event.target.value);
    };

    const handleFilterValueChange = (event) => {
        setFilterValue(event.target.value);
    };

    const handleStatusChange = (event, bookingId) => {
        const newStatus = event.target.value;
        updateStatus(bookingId, newStatus);
    };

    const LoginButton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            Logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            Login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <div className="sidebar">
                {/* ... */}
            </div>

            <section className="home-section">
                <nav>
                    {/* ... */}
                </nav>

                <div className="salecartableDivrq">
                    <div className="filter-options">
                        <label htmlFor="filterBy">Filter By:</label>
                        <select id="filterBy" value={filterBy} onChange={handleFilterChange}>
                            <option value="requestDay">Request Day</option>
                            <option value="pickupDate">Pickup Date</option>
                            <option value="car">Car</option>
                        </select>

                        <label htmlFor="filterValue">Filter Value:</label>
                        <input type="text" id="filterValue" value={filterValue} onChange={handleFilterValueChange} />
                    </div>

                    <h1 className="heading"><span>Booking Requests</span></h1>
                    <div className="table-container">
                        <table className="salecartable">
                            <thead>
                                <tr>
                                    <th>User's Email</th>
                                    <th>Phone</th>
                                    <th>Car</th>
                                    <th>Status</th>
                                    <th>Request date</th>
                                    <th>Request Exact Time</th>
                                    <th>Pickup Date</th>
                                    <th>Return Date</th>
                                    <th>DELETE</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredBookings.map((booking) => (
                                    <tr key={booking._id}>
                                        <td>{booking.user}</td>
                                        <td>{booking.phone}</td>
                                        <td>{booking.car}</td>
                                        <td>
                                            <select
                                                value={booking.status}
                                                onChange={(e) => handleStatusChange(e, booking._id)}
                                            >
                                                <option value="Pending">Pending</option>
                                                <option value="Approved">Approved</option>
                                                <option value="Rejected">Rejected</option>
                                            </select>
                                        </td>
                                        <td>{booking.createdAt.split('T')[0]}</td>
                                        <td>{booking.createdAt.split('T')[1]}</td>
                                        <td>{booking.pickupDate.split('T')[0]}</td>
                                        <td>{booking.returnDate.split('T')[0]}</td>
                                        <td>
                                            <button
                                                className="btn" id="delete"
                                                onClick={() => deleteBooking(booking._id)}
                                            ><i className="fa fa-trash"></i>
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </section>
        </>
    );
};

export default AdminBookingRequests;
