import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useLocation, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const MoreDetails = () => {
    const { state, dispatch } = useContext(UserContext);
    const history = useHistory();

    let location = useLocation();
    const selectedCarId = location.state;
    const [userData, setUserData] = useState({
        id: '',
        name: '',
        email: '',
        message: '',
    });
    const [renttcarsData, setrenttcarsData] = useState({
        id: '',
        brand: '',
        model: '',
        year: '',
        color: '',
        seats: '',
        rent: '',
        fileName: '',
        filePath: '',
        fileType: '',
        fileSize: '',
        description: '',
    });
    const [allrenttcarReviews, setAllrenttcarReviews] = useState([]);
    const [cartItems, setCartItems] = useState([]);

    const sendId = async () => {
        try {
            const res = await fetch('/sendReviewRentCarId', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    selectedCarId,
                }),
            });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        sendId();
    }, []);

    const reviewCarData = async () => {
        try {
            const res = await fetch('/getRentCarReviews', {
                method: 'GET',
            });

            const data = await res.json();
            setrenttcarsData({
                id: data.findCar._id,
                brand: data.findCar.brand,
                model: data.findCar.model,
                year: data.findCar.year,
                color: data.findCar.color,
                seats: data.findCar.seats,
                rent: data.findCar.rent,
                fileName: data.findCar.fileName,
                filePath: data.findCar.filePath,
                fileType: data.findCar.fileType,
                fileSize: data.findCar.fileSize,
                description: data.findCar.description,
            });

            setUserData({
                ...userData,
                id: data.findUser._id,
                name: data.findUser.name,
                email: data.findUser.email,
            });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        reviewCarData();
    }, []);

    const getallreviews = async () => {
        try {
            const res = await fetch('/getallreviewsforselectedrentcar', {
                method: 'GET',
            });

            const data = await res.json();

            setAllrenttcarReviews(data.allReviews);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getallreviews();
    }, []);

    const handleInputs = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setUserData({ ...userData, [name]: value });
    };

    const submitReviews = async (e) => {
        e.preventDefault();

        const { id, name, email, message } = userData;

        if (!name || !email || !message) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Message',
                text: 'Please fill in all the fields',
                showConfirmButton: true,
            });
            return;
        }

        const res = await fetch('/postrentcarreviews', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                id,
                name,
                email,
                message,
                selectedCarId,
            }),
        });

        const data = await res.json();

        if (data.status === 500 || !data) {
            Swal.fire({
                icon: 'error',
                title: 'Review not submitted',
                showConfirmButton: false,
                timer: 1500,
            });
            console.log('reviews not submitted');
        } else if (data.status === 201) {
            Swal.fire({
                icon: 'success',
                title: 'Review submitted',
                showConfirmButton: false,
                timer: 1500,
            });
            setUserData({ ...userData, message: '' });
            history.go(0); // Refresh the page
        } else {
            Swal.fire({
                icon: 'success',
                title: 'Review submitted',
                showConfirmButton: false,
                timer: 1500,
            });
            setUserData({ ...userData, message: '' });
            history.go(0); // Refresh the page
        }
    };

    const addToCart = () => {
        const itemExists = cartItems.find((item) => item.id === renttcarsData.id);

        if (itemExists) {
            console.log('Car is already in the cart');
        } else {
            const newItem = {
                id: renttcarsData.id,
                brand: renttcarsData.brand,
                model: renttcarsData.model,
                year: renttcarsData.year,
                color: renttcarsData.color,
                seats: renttcarsData.seats,
                rent: renttcarsData.rent,
                fileName: renttcarsData.fileName,
                filePath: renttcarsData.filePath,
                fileType: renttcarsData.fileType,
                fileSize: renttcarsData.fileSize,
                description: renttcarsData.description,
            };

            setCartItems([...cartItems, newItem]);
            console.log('Car added to cart:', newItem);
        }
    };

    const proceedToCart = () => {
        history.push('/cart'); // Redirect to the cart page
    };

    const Loginbutton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <header className="header">
                <div id="menu-btn" className="fas fa-bars"></div>
                <NavLink className="logo" to="/">
                    <span>Krily</span>Car{' '}
                </NavLink>
                <nav className="navbar">
                    <NavLink to="/">Home</NavLink>
                    <NavLink to="/rentcar">Rent A Car</NavLink>
                </nav>
                <div id="login-btn">
                    <Loginbutton />
                </div>
            </header>

            <div className="reviewsdiv" style={{ marginTop: '50px' }}>
                <h2>Selected Car Details:</h2>
                <hr />

                <div className="selected-car-details">
                    <div className="car-image">
                        <img src={renttcarsData.filePath} alt={renttcarsData.fileName} />
                    </div>

                    <div className="car-info">
                        <h2>
                            {renttcarsData.brand} {renttcarsData.model}
                        </h2>
                        <p>
                            <strong>Year:</strong> {renttcarsData.year}
                        </p>
                        <p>
                            <strong>Color:</strong> {renttcarsData.color}
                        </p>
                        <p>
                            <strong>Seats:</strong> {renttcarsData.seats}
                        </p>
                        <p>
                            <strong>Rent:</strong> {renttcarsData.rent}
                        </p>

                        <div className="buttons">
                            <button onClick={addToCart}>Add to Cart</button>
                            <button onClick={proceedToCart}>Proceed to Cart</button>
                        </div>
                    </div>
                </div>
            </div>

            <div className="reviewsdiv">
                <h2>Customer Reviews:</h2>
                <hr />

                <div className="reviews">
                    {allrenttcarReviews.map((review) => (
                        <div key={review._id}>
                            <h4>{review.name}</h4>
                            <p>{review.message}</p>
                        </div>
                    ))}
                </div>
            </div>

            <div className="reviewsdiv">
                <h2>Post Your Review:</h2>
                <hr />

                <form method="POST" className="reviews-form">
                    <div>
                        <label htmlFor="name">Name:</label>
                        <input
                            type="text"
                            name="name"
                            id="name"
                            autoComplete="off"
                            value={userData.name}
                            onChange={handleInputs}
                        />
                    </div>
                    <div>
                        <label htmlFor="email">Email:</label>
                        <input
                            type="email"
                            name="email"
                            id="email"
                            autoComplete="off"
                            value={userData.email}
                            onChange={handleInputs}
                        />
                    </div>
                    <div>
                        <label htmlFor="message">Message:</label>
                        <textarea
                            name="message"
                            id="message"
                            value={userData.message}
                            onChange={handleInputs}
                        ></textarea>
                    </div>
                    <div>
                        <button type="submit" className="btn btn-primary" onClick={submitReviews}>
                            Submit
                        </button>
                    </div>
                </form>
            </div>
        </>
    );
};

export default MoreDetails;
