import React, { useEffect, useState, useContext } from 'react';
import { NavLink } from "react-router-dom";
import { AdminContext } from "../../App"

const DisplayAllMessages = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext)
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        fetch('/messages')
            .then(response => response.json())
            .then(data => setMessages(data))
            .catch(error => console.error(error));
    }, []);
    const Loginbutton = () => {

        if (adminState) {
            return <div>
                <button className="logoutbtnDash"><NavLink className="nav-link" to="/adminsignout">logout</NavLink></button>
            </div>
        }
        else {
            return <div>
                <button className="logoutbtnDash"><NavLink className="nav-link" to="/signin">login</NavLink></button>

            </div>
        }
    }
    return (
        <>

            <div>
                <h1>All Messages</h1>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>Message</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {messages.map((message, index) => (
                            <tr key={index}>
                                <td>{message.name}</td>
                                <td>{message.email}</td>
                                <td>{message.phone}</td>
                                <td>{message.message}</td>
                                <td>
                                    <button>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default DisplayAllMessages;
