import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const BookingRequest = () => {
    const [bookingData, setBookingData] = useState({
        name: '',
        email: '',
        phone: '',
        pickupDate: '',
        returnDate: '',
        car: '',
    });

    const { state } = useContext(UserContext);
    const history = useHistory();

    useEffect(() => {
        if (!state) {
            Swal.fire({
                title: 'Please sign in to make a booking request!',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'OK',
            }).then((result) => {
                if (result.isConfirmed) {
                    history.push('/signin');
                } else {
                    history.push('/');
                }
            });
        }
    }, [state, history]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBookingData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Perform booking request submission logic
    };

    useEffect(() => {
        if (!state) {
            history.push('/signin'); // Redirect to login page if user is not logged in
        }
    }, [state, history]);

    return (
        <>
            {state && (
                <div>
                    <nav className="navbar">
                        <NavLink to="/">Home</NavLink>
                        <NavLink to="/rentcar">Rent A Car</NavLink>
                        <NavLink to="/about">About</NavLink>
                        <a href="/#services">Testimonial</a>
                        <a href="/contact">Contact</a>
                        <div id="login-btn">
                            {state ? (
                                <button>
                                    <NavLink className="btn" to="/signout">
                                        Logout
                                    </NavLink>
                                </button>
                            ) : (
                                <button>
                                    <NavLink className="btn" to="/signin">
                                        Login
                                    </NavLink>
                                </button>
                            )}
                        </div>
                    </nav>
                    <section className="booking-form">
                        <h1 className="heading">Booking Request</h1>
                        <form onSubmit={handleSubmit}>
                            <label>
                                Name:
                                <input
                                    type="text"
                                    name="name"
                                    value={bookingData.name}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Email:
                                <input
                                    type="email"
                                    name="email"
                                    value={bookingData.email}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Phone:
                                <input
                                    type="tel"
                                    name="phone"
                                    value={bookingData.phone}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Pickup Date:
                                <input
                                    type="date"
                                    name="pickupDate"
                                    value={bookingData.pickupDate}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Return Date:
                                <input
                                    type="date"
                                    name="returnDate"
                                    value={bookingData.returnDate}
                                    onChange={handleChange}
                                />
                            </label>
                            <label>
                                Car:
                                <select
                                    name="car"
                                    value={bookingData.car}
                                    onChange={handleChange}
                                >
                                    <option value="">Select a car</option>
                                    {carList.map((car, index) => (
                                        <option key={index} value={car}>
                                            {car}
                                        </option>
                                    ))}
                                </select>
                            </label>
                            <button type="submit">Submit</button>
                        </form>
                    </section>
                </div>
            )}
        </>
    );
};

export default BookingRequest;
