import React, { useState, useEffect, useContext, useRef } from 'react';
import { NavLink } from 'react-router-dom';

import { AdminContext } from '../../App';

const Statistics = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [income, setIncome] = useState([]);
    const [carCount, setCarCount] = useState({});
    const [incomeChartData, setIncomeChartData] = useState([]);
    const carCountChartRef = useRef(null);
    const incomeChartRef = useRef(null);

    const getRentCarIncome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET',
            });

            const data = await res.json();

            setIncome(data);

            // Calculate the count for each car
            const count = {};
            data.forEach((item) => {
                item.soldItems.forEach((soldItem) => {
                    const { brand, model } = soldItem;
                    const carKey = `${brand}-${model}`;
                    count[carKey] = count[carKey] ? count[carKey] + 1 : 1;
                });
            });

            setCarCount(count);
        } catch (error) {
            console.log(error);
        }
    };

    const formatIncomeChartData = () => {
        const chartData = income.map((item) => {
            return item.soldItems.map((soldItem) => {
                return {
                    brand: soldItem.brand,
                    model: soldItem.model,
                    totalIncome: soldItem.totalIncome,
                };
            });
        });

        const flattenedData = [].concat(...chartData);

        const groupedData = flattenedData.reduce((acc, item) => {
            const { brand, model } = item;
            const carKey = `${brand}-${model}`;
            if (acc[carKey]) {
                acc[carKey].totalIncome += item.totalIncome;
            } else {
                acc[carKey] = {
                    brand,
                    model,
                    totalIncome: item.totalIncome,
                };
            }
            return acc;
        }, {});

        const chartValues = Object.values(groupedData);

        const sortedChartData = chartValues.sort((a, b) => b.totalIncome - a.totalIncome);

        return sortedChartData;
    };

    useEffect(() => {
        getRentCarIncome();
    }, []);

    useEffect(() => {
        if (Object.keys(carCount).length > 0) {
            // Create chart for car count
            const countLabels = Object.keys(carCount);
            const countValues = Object.values(carCount);

            const countChartConfig = {
                type: 'bar',
                data: {
                    labels: countLabels,
                    datasets: [
                        {
                            label: 'Rented times',
                            data: countValues,
                            backgroundColor: 'rgba(228, 87, 46)',
                            borderWidth: 1,
                        },
                    ],
                },
                options: {
                    responsive: true,
                    scales: {
                        y: {
                            beginAtZero: true,
                        },
                    },
                    indexAxis: 'y',
                    barThickness: 'flex',
                },
            };

            const countChart = new window.Chart(carCountChartRef.current, countChartConfig);

            return () => {
                countChart.destroy();
            };
        }
    }, [carCount]);

    useEffect(() => {
        if (income.length > 0) {
            // Format income chart data
            const chartData = formatIncomeChartData();
            setIncomeChartData(chartData);

            // Create chart for income
            const incomeLabels = chartData.map((item) => `${item.brand}-${item.model}`);
            const incomeValues = chartData.map((item) => item.totalIncome);

            const incomeChartConfig = {
                type: 'bar',
                data: {
                    labels: incomeLabels,
                    datasets: [
                        {
                            label: 'Total Income (DA)',
                            data: incomeValues,
                            backgroundColor: 'rgba(73, 131, 191)',
                            borderWidth: 1,
                        },
                    ],
                },
                options: {
                    responsive: true,
                    scales: {
                        y: {
                            beginAtZero: true,
                        },
                    },
                    indexAxis: 'y',
                    barThickness: 'flex',
                },
            };

            const incomeChart = new window.Chart(incomeChartRef.current, incomeChartConfig);

            return () => {
                incomeChart.destroy();
            };
        }
    }, [income]);

    const Loginbutton = () => {
        // Login button logic
    };

    return (
        <>
            <div className="sidebar">
                {/* Sidebar content */}
            </div>
            <section className="home-section">
                <nav>
                    {/* Navigation content */}
                </nav>
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Most Rented Cars</span>
                    </h1>

                    <div className="chartContainer">
                        <canvas ref={carCountChartRef}></canvas>
                    </div>

                    <div className="totalIncomeTableDiv">
                        <h1 className="heading">
                            <span>Total Income by Car</span>
                        </h1>

                        {incomeChartData.length > 0 && (
                            <div className="chartContainer">
                                <canvas ref={incomeChartRef}></canvas>
                            </div>
                        )}

                        <table className="totalIncomeTable">
                            <thead>
                                <tr>
                                    <th>BRAND</th>
                                    <th>MODEL</th>
                                    <th>TOTAL INCOME</th>
                                </tr>
                            </thead>

                            <tbody>
                                {incomeChartData.map((item) => (
                                    <tr key={`${item.brand}-${item.model}`}>
                                        <td>{item.brand}</td>
                                        <td>{item.model}</td>
                                        <td>{item.totalIncome} DA</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </section>
        </>
    );
};

export default Statistics;
