import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Stripe from 'react-stripe-checkout';

import { UserContext } from '../App';

const Rentcarcart = () => {
    const { state, dispatch } = useContext(UserContext);

    const [cartUser, setCartUser] = useState([]);
    const [items, setItems] = useState([]);
    let itemsPrice, idOfRentedCar, reqDays;

    const getCartData = async () => {
        try {
            const res = await fetch('/getRentCartData', {
                method: 'POST',
            });

            const data = await res.json();
            setCartUser(data.userById);
            setItems(data.cartItems);

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getCartData();
    }, []);

    items.map((item) => {
        itemsPrice = item.totalbill;
        idOfRentedCar = item.rentcarid;
        reqDays = item.requireddays;
    });

    const handlePayMethod = (itemsPrice, token) => {
        return fetch('/stripeRentPay', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                token: token.id,
                amount: itemsPrice,
                idRentedCar: idOfRentedCar,
                daysRequired: reqDays,
            }),
        });
    };

    const tokenHandler = (token) => {
        handlePayMethod(itemsPrice, token);
        updateDataBase();
    };

    const updateDataBase = () => {
        return fetch('/updateRentDataBase', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                items,
            }),
        });
    };

    const handleDeleteItem = async (itemId) => {
        try {
            // Remove the item from the list on the client-side
            const updatedItems = items.filter((item) => item._id !== itemId);
            setItems(updatedItems);

            // Send a request to the server to delete the item from the database
            const res = await fetch('/deleteItemFromDatabase', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    itemId,
                }),
            });

            if (res.status === 200) {
                console.log('Item deleted successfully from the database.');
            } else {
                console.log('Failed to delete item from the database.');
            }
        } catch (error) {
            console.log(error);
        }
    };

    const Loginbutton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <header className="header">
                <div id="menu-btn" className="fas fa-bars"></div>
                <NavLink className="logo" to="/">
                    <span>Car</span>Rent
                </NavLink>

                <nav className="navbar">
                    <NavLink to="/">Home</NavLink>
                    <NavLink to="/rentcar">Rent Cars</NavLink>
                </nav>

                <div id="login-btn">
                    <Loginbutton />
                </div>
            </header>

            <div className="salecartMaindiv">
                <div style={{ marginTop: '150px' }}>
                    {items.map((item) => (
                        <div className="salecartLidiv" key={item._id}>
                            <ul>
                                <li style={{ wordSpacing: '10px' }}>
                                    Brand: {item.brand} --- Model: {item.model} --- Days: {item.requireddays} --- RentPerDay: {item.rentperday}Taka --- TotalBill: {item.totalbill}Taka
                                    <button className="btn" onClick={() => handleDeleteItem(item._id)}>
                                        <i className="fa fa-trash"></i>
                                    </button>
                                </li>
                            </ul>
                        </div>
                    ))}
                    <div style={{ padding: '30px', textAlign: 'center' }}>
                        <h2>Pay Through Credit / Debit Card</h2>
                        <br />
                        <Stripe stripeKey="pk_test_51Jyb5UBvc4Qazj8jy6qimLop4epxe5jziUD3ixj5ISycjjD6yYVGZhk688Pz9Lna32VTHbSHxRwkrvNNnnnr96P000M68u5jcd" token={tokenHandler} />
                    </div>
                </div>
            </div>
        </>
    );
};

export default Rentcarcart;
