import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useLocation, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const MoreDetails = () => {
    const { state, dispatch } = useContext(UserContext);
    const history = useHistory();
    const [rentCarsData, setRentCarsData] = useState([]);
    let location = useLocation();
    const selectedCarId = location.state;
    const [userData, setUserData] = useState({
        id: '',
        name: '',
        email: '',
        message: '',
    });
    const [renttcarsData, setRenttcarsData] = useState({
        id: '',
        brand: '',
        model: '',
        year: '',
        color: '',
        seats: '',
        rent: '',
        fileName: '',
        filePath: '',
        fileType: '',
        fileSize: '',
        description: '',
    });
    const [allrenttcarReviews, setAllrenttcarReviews] = useState([]);

    const reviewCarData = async () => {
        try {
            const res = await fetch('/getRentCarReviews', {
                method: 'GET',
            });

            const data = await res.json();
            setRenttcarsData({
                id: data.findCar._id,
                brand: data.findCar.brand,
                model: data.findCar.model,
                year: data.findCar.year,
                color: data.findCar.color,
                seats: data.findCar.seats,
                rent: data.findCar.rent,
                fileName: data.findCar.fileName,
                filePath: data.findCar.filePath,
                fileType: data.findCar.fileType,
                fileSize: data.findCar.fileSize,
                description: data.findCar.description,
            });

            setUserData({
                ...userData,
                id: data.findUser._id,
                name: data.findUser.name,
                email: data.findUser.email,
            });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        if (selectedCarId) {
            reviewCarData();
        }
    }, [selectedCarId]);

    const Loginbutton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    const [rentDays, setRentDays] = useState('');
    const handleInputs = (e) => {
        let value = e.target.value;
        setRentDays(value);
    };

    const addToCart = async (e) => {
        e.preventDefault();
        const currentCar = e.target.id;

        if (
            formDiv[currentCar].style.display === 'none' &&
            specsDiv[currentCar].style.display === 'none' &&
            carDiv[currentCar].style.display === 'block'
        ) {
            carDiv[currentCar].style.display = 'none';
            specsDiv[currentCar].style.display = 'none';
            formDiv[currentCar].style.display = 'block';
        } else {
            formDiv[currentCar].style.display = 'none';
            specsDiv[currentCar].style.display = 'none';
            carDiv[currentCar].style.display = 'block';
        }
    };

    const showCarAgain = async (e) => {
        e.preventDefault();
        const currentCar = e.target.id;

        if (
            formDiv[currentCar].style.display === 'block' &&
            specsDiv[currentCar].style.display === 'none' &&
            carDiv[currentCar].style.display === 'none'
        ) {
            carDiv[currentCar].style.display = 'block';
            specsDiv[currentCar].style.display = 'none';
            formDiv[currentCar].style.display = 'none';
        } else {
            formDiv[currentCar].style.display = 'block';
            specsDiv[currentCar].style.display = 'none';
            carDiv[currentCar].style.display = 'none';
        }
    };

    const proceedToCart = async (e) => {
        e.preventDefault();
        let itemId = e.target.id;

        const res = await fetch('/addrentcartocart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                itemId,
                rentDays,
            }),
        });
        const data = await res.json();

        if (res.status === 500 || !data) {
            Swal.fire({
                title: 'Something went wrong',
                icon: 'error',
                confirmButtonText: 'OK',
            });
        } else if (res.status === 400 && data.error === 'Item is already in the cart') {
            Swal.fire({
                title: 'Item is already in the cart',
                icon: 'warning',
                confirmButtonText: 'OK',
            }).then((result) => {
                history.push('/rentcarcart');
            });
        } else if (res.status === 400 && data.error === 'Item is not available for rent') {
            Swal.fire({
                title: 'Item is not available for rent',
                icon: 'warning',
                confirmButtonText: 'OK',
            });
        } else if (res.status === 201) {
            Swal.fire({
                title: 'Item added to cart',
                icon: 'success',
                confirmButtonText: 'OK',
            }).then((result) => {
                history.push('/rentcarcart');
            });
        }
    };

    return (
        <>
            <header className="header">
                <div id="menu-btn" className="fas fa-bars"></div>
                <NavLink className="logo" to="/">
                    <span>Krily</span>Car{' '}
                </NavLink>
                <nav className="navbar">
                    <NavLink to="/">Home</NavLink>
                    <NavLink to="/rentcar">Rent A Car</NavLink>
                </nav>
                <div id="login-btn">
                    <Loginbutton />
                </div>
            </header>

            <div className="reviewsdiv" style={{ marginTop: '50px' }}>
                <h2>Car Details</h2>
                <img src={renttcarsData.filePath} alt="" style={{ width: '50%', height: '50%' }} />
                <h4>
                    <b>{renttcarsData.brand}</b>
                </h4>
                <p>Model: {renttcarsData.model}</p>
                <p>Year: {renttcarsData.year}</p>
                <p>Color: {renttcarsData.color}</p>
                <p>Seats: {renttcarsData.seats}</p>
                <p>Rent: {renttcarsData.rent} DA</p>
                <p>Description: {renttcarsData.description}</p>
                <div className="rentcarcard">
                    {rentCarsData.map((rentCar, index) => (
                        <div className="cardiv" id={'carDiv' + index}>
                            <img
                                src={rentCar.filePath}
                                alt="Car Image"
                                className="rentcarcardimage"
                            />
                            <div className="carcontentdiv">
                                <h4>
                                    <b>{rentCar.brand}</b>
                                </h4>
                                <p>
                                    <b>Model:</b> {rentCar.model}
                                </p>
                                <p>
                                    <b>Year:</b> {rentCar.year}
                                </p>
                                <p>
                                    <b>Color:</b> {rentCar.color}
                                </p>
                                <p>
                                    <b>Seats:</b> {rentCar.seats}
                                </p>
                                <p>
                                    <b>Rent:</b> {rentCar.rent} DA/day
                                </p>
                                <button
                                    className="cartbtn"
                                    id={index}
                                    onClick={(e) => addToCart(e)}
                                >
                                    Rent Now
                                </button>
                                <div className="carreviewdiv">
                                    <NavLink to={'/rentcarreview/' + rentCar._id}>
                                        See reviews
                                    </NavLink>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="formdiv">
                    {rentCarsData.map((rentCar, index) => (
                        <div className="cartformdiv" id={'formDiv' + index}>
                            <form>
                                <h2>Enter Rental Period (in days)</h2>
                                <input
                                    type="number"
                                    className="rentdaysinput"
                                    name="rentDays"
                                    placeholder="Enter Rental Period"
                                    autoComplete="off"
                                    onChange={handleInputs}
                                />
                                <button
                                    type="submit"
                                    className="proceedbtn"
                                    id={rentCar._id}
                                    onClick={(e) => proceedToCart(e)}
                                >
                                    Proceed to Cart
                                </button>
                                <button
                                    type="submit"
                                    className="gobackbtn"
                                    id={index}
                                    onClick={(e) => showCarAgain(e)}
                                >
                                    Go Back
                                </button>
                            </form>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
};

export default MoreDetails;
