import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

const Addcars = () => {
    const [car, setCar] = useState({
        brand: '',
        model: '',
        year: '',
        color: '',
        enginecc: '',
        maxpower: '',
        price: '',
        retailprice: '',
        quantity: '',
        description: '', // Added "description" field
    });

    const handleInputs = (e) => {
        const { name, value } = e.target;
        setCar({ ...car, [name]: value });
    };

    const postData = async (e) => {
        e.preventDefault();
        const { brand, model, year, color, enginecc, maxpower, price, retailprice, quantity, description } = car;

        try {
            const res = await fetch('/addcars', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    brand,
                    model,
                    year,
                    color,
                    enginecc,
                    maxpower,
                    price,
                    retailprice,
                    quantity,
                    description, // Added "description" field
                }),
            });

            const data = await res.json();

            if (res.status === 200) {
                window.alert(data.message);
                setCar({
                    brand: '',
                    model: '',
                    year: '',
                    color: '',
                    enginecc: '',
                    maxpower: '',
                    price: '',
                    retailprice: '',
                    quantity: '',
                    description: '', // Added "description" field
                });
            } else {
                window.alert('Failed to add car');
            }
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <>
            <form method="POST" className="container">
                <div className="row mb-3">
                    <label htmlFor="brand" className="col-sm-2 col-form-label">Brand</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="brand" name="brand" value={car.brand} onChange={handleInputs} placeholder="Enter car brand" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="model" className="col-sm-2 col-form-label">Model</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="model" name="model" value={car.model} onChange={handleInputs} placeholder="Enter car model" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="year" className="col-sm-2 col-form-label">Year</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="year" name="year" value={car.year} onChange={handleInputs} placeholder="Enter car year" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="color" className="col-sm-2 col-form-label">Color</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="color" name="color" value={car.color} onChange={handleInputs} placeholder="Enter car color" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="enginecc" className="col-sm-2 col-form-label">Engine CC</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="enginecc" name="enginecc" value={car.enginecc} onChange={handleInputs} placeholder="Enter engine CC" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="maxpower" className="col-sm-2 col-form-label">Max Power</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="maxpower" name="maxpower" value={car.maxpower} onChange={handleInputs} placeholder="Enter max power" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="price" className="col-sm-2 col-form-label">Price</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="price" name="price" value={car.price} onChange={handleInputs} placeholder="Enter car price" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="retailprice" className="col-sm-2 col-form-label">Retail Price</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="retailprice" name="retailprice" value={car.retailprice} onChange={handleInputs} placeholder="Enter retail price" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="quantity" className="col-sm-2 col-form-label">Quantity</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="quantity" name="quantity" value={car.quantity} onChange={handleInputs} placeholder="Enter car quantity" required />
                    </div>
                </div>
                <div className="row mb-3">
                    <label htmlFor="description" className="col-sm-2 col-form-label">Description</label>
                    <div className="col-sm-10">
                        <textarea className="form-control" id="description" name="description" value={car.description} onChange={handleInputs} placeholder="Enter car description" required></textarea> // Added "description" field
                    </div>
                </div>

                <div className="text-center">
                    <button type="submit" className="btn btn-primary" onClick={postData}>Add Car</button>
                    <NavLink to="/cars" className="btn btn-secondary mx-2">Back</NavLink>
                </div>
            </form>
        </>
    );
};

export default Addcars;
