import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import Stripe from 'react-stripe-checkout';

import { UserContext } from '../App';

const MyRentCode = () => {

    const { state, dispatch } = useContext(UserContext);
    const [setIncomeUser] = useState([]);
    const [items, setItems] = useState([]);
    let mycode;
    const getRentCode = async () => {
        try {
            const res = await fetch('/getMyIncomes', {
                method: 'GET',
            });
            const data = await res.json();
            setIncomeUser(data.userById);
            setItems(data.soldItems);
            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };
    useEffect(() => {
        getRentCode();
    }, []);
    items.map(items => {
        mycode = items.rentCode
    });


    const Loginbutton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };
    return (
        <>
            <header className="header">
                <div id="menu-btn" className="fas fa-bars"></div>
                <NavLink className="logo" to="/">
                    <span>Car</span>Rent
                </NavLink>

                <nav className="navbar">
                    <NavLink to="/">Home</NavLink>
                    <NavLink to="/rentcar">Rent A Car</NavLink>
                </nav>

                <div id="login-btn">
                    <Loginbutton />
                </div>
            </header>
            <div>
                <h1>Rent Code : {mycode}</h1>
            </div>
        </>
    )
};

export default MyRentCode;
