import React, { useState, useEffect, useContext, useRef } from 'react';
import { NavLink } from 'react-router-dom';

import { AdminContext } from '../../App';

const Statistics = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [income, setIncome] = useState([]);
    const [carCount, setCarCount] = useState({});
    const chartRef = useRef(null);

    const getrentcarincome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET',
            });

            const data = await res.json();

            setIncome(data);

            // Calculate the count for each car
            const count = {};
            data.forEach((item) => {
                item.soldItems.forEach((soldItem) => {
                    const { brand, model } = soldItem;
                    const carKey = `${brand}-${model}`;
                    count[carKey] = count[carKey] ? count[carKey] + 1 : 1;
                });
            });

            setCarCount(count);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getrentcarincome();
    }, []);

    useEffect(() => {
        if (Object.keys(carCount).length > 0) {
            // Create chart
            const labels = Object.keys(carCount);
            const values = Object.values(carCount);

            const chartConfig = {
                type: 'bar',
                data: {
                    labels,
                    datasets: [
                        {
                            label: 'Repeat Count',
                            data: values,
                            backgroundColor: 'rgba(75, 192, 192, 0.6)',
                            borderWidth: 1,
                        },
                    ],
                },
                options: {
                    responsive: true,
                    scales: {
                        y: {
                            beginAtZero: true,
                        },
                    },
                },
            };

            const chart = new window.Chart(chartRef.current, chartConfig);

            // Cleanup
            return () => {
                chart.destroy();
            };
        }
    }, [carCount]);

    const Loginbutton = () => {
        // Login button logic
    };

    return (
        <>
            {/* Sidebar JSX */}
            <section className="home-section">
                {/* Header and navigation JSX */}
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Rented Cars Income Report</span>
                    </h1>

                    <table className="salecartable">
                        {/* Table content */}
                    </table>

                    {/* Bar chart */}
                    <div className="chartContainer">
                        <canvas ref={chartRef}></canvas>
                    </div>
                </div>
            </section>
        </>
    );
};

export default Statistics;
