import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import Swal from 'sweetalert2';
import { AdminContext } from '../../App';

const Getrentcars = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);
    const [getCars, setGetCars] = useState([
        {
            _id: '',
            brand: '',
            model: '',
            rent: '',
            price: '',
            availability: '',
            editedPrice: '',
            newRent: '', // New state variable for new rent value
            newAvailability: '', // New state variable for new availability value
        },
    ]);

    const getallrenttcars = async () => {
        try {
            const res = await fetch('/getAvailableRentCars', {
                method: 'GET',
            });

            const data = await res.json();
            const carsWithEditedPrice = data.map((car) => ({
                ...car,
                editedPrice: '',
                newRent: '', // Initialize new rent for each car
                newAvailability: '', // Initialize new availability for each car
            }));
            setGetCars(carsWithEditedPrice);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getallrenttcars();
    }, []);

    const deleteUser = async (e) => {
        const carIdFromDashBoard = e.target.id;

        try {
            await fetch('/deleteRentCarFromDashboard', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                }),
            });

            // Optional: Refresh the car list after deleting
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const setNewRentHandler = async (e, index) => {
        const carIdFromDashBoard = e.target.id;
        const newRent = getCars[index].newRent;

        if (!newRent) {
            // Show an error SweetAlert notification
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'Please enter a valid rent value',
            });
            return;
        }

        try {
            await fetch('/updateCarRent', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                    newRent,
                }),
            });

            // Show a success SweetAlert notification
            Swal.fire({
                icon: 'success',
                title: 'Rent Updated',
                text: 'The rent has been updated successfully!',
            });

            // Optional: Refresh the car list after updating the rent
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const setNewAvailabilityHandler = async (e, index) => {
        const carIdFromDashBoard = e.target.id;
        const newAvailability = getCars[index].newAvailability;

        if (!newAvailability) {
            // Show an error SweetAlert notification
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'Please enter a valid availability value',
            });
            return;
        }

        try {
            await fetch('/updateCarAvailability', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                    newAvailability,
                }),
            });

            // Show a success SweetAlert notification
            Swal.fire({
                icon: 'success',
                title: 'Availability Updated',
                text: 'The availability has been updated successfully!',
            });

            // Optional: Refresh the car list after updating the availability
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const resetAvailabilityHandler = async (e, index) => {
        const carIdFromDashBoard = e.target.id;

        try {
            await fetch('/updateCarAvailability', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    carIdFromDashBoard,
                    newAvailability: 'reset',
                }),
            });

            // Show a success SweetAlert notification
            Swal.fire({
                icon: 'success',
                title: 'Availability Reset',
                text: 'The availability has been reset to the default value!',
            });

            // Optional: Refresh the car list after resetting the availability
            getallrenttcars();
        } catch (error) {
            console.log(error);
        }
    };

    const Loginbutton = () => {
        if (adminState) {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/adminsignout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button className="logoutbtnDash">
                        <NavLink className="nav-link" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    return (
        <>
            <div className="sidebar">
                {/* Remaining code unchanged */}
            </div>

            <section className="home-section">
                {/* Remaining code unchanged */}
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Available Rent Cars</span>
                    </h1>

                    <table className="salecartable">
                        <thead>
                            <tr>
                                <th>BRAND</th>
                                <th>MODEL</th>
                                <th>RENT</th>
                                <th>PRICE</th>
                                <th>AVAILABILITY</th>
                                <th>EDIT PRICE</th>
                                <th>DELETE</th>
                            </tr>
                        </thead>

                        {getCars.map((car, index) => (
                            <tbody key={car._id}>
                                <tr>
                                    <td>{car.brand}</td>
                                    <td>{car.model}</td>
                                    <td>{car.rent}</td>
                                    <td>{car.price} DA</td>
                                    <td>{car.availability} days</td>
                                    <td>
                                        <input
                                            type="number"
                                            value={car.newRent}
                                            onChange={(e) =>
                                                setGetCars((prevCars) =>
                                                    prevCars.map((prevCar, prevIndex) =>
                                                        prevIndex === index
                                                            ? { ...prevCar, newRent: e.target.value }
                                                            : prevCar
                                                    )
                                                )
                                            }
                                        />
                                        <button
                                            id={car._id}
                                            onClick={(e) => setNewRentHandler(e, index)}
                                            className="btn"
                                        >
                                            <i className="fa fa-pencil"></i>
                                        </button>
                                    </td>
                                    <td>
                                        <input
                                            type="number"
                                            value={car.newAvailability}
                                            onChange={(e) =>
                                                setGetCars((prevCars) =>
                                                    prevCars.map((prevCar, prevIndex) =>
                                                        prevIndex === index
                                                            ? { ...prevCar, newAvailability: e.target.value }
                                                            : prevCar
                                                    )
                                                )
                                            }
                                        />
                                        <button
                                            id={car._id}
                                            onClick={(e) => setNewAvailabilityHandler(e, index)}
                                            className="btn"
                                        >
                                            <i className="fa fa-pencil"></i>
                                        </button>
                                        <button
                                            id={car._id}
                                            onClick={(e) => resetAvailabilityHandler(e, index)}
                                            className="btn"
                                        >
                                            Reset
                                        </button>
                                    </td>
                                    <td>
                                        <button
                                            id={car._id}
                                            onClick={deleteUser}
                                            className="btn"
                                        >
                                            <i className="fa fa-trash"></i>
                                        </button>
                                    </td>
                                </tr>
                            </tbody>
                        ))}
                    </table>
                </div>
            </section>
        </>
    );
};

export default Getrentcars;
