import React, { useState, useEffect, useContext, createContext, } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';



//see my requests
const UserBookingRequests = () => {
    const [bookingRequests, setBookingRequests] = useState([]);
    const { state, user } = useContext(UserContext);

    const history = useHistory();
    useEffect(() => {
        if (!state) {
            Swal.fire({
                title: 'Please signin to track your requests!',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'OK'
            }).then((result) => {
                if (result.isConfirmed) {
                    history.push('/signin');
                } else { history.push('/'); }
            });
        }
    }, [state, history]);

    const getBookingRequests = async () => {
        try {
            const res = await fetch('/getStatus');
            const data = await res.json();
            setBookingRequests(data.bookings);
        } catch (error) {
            console.log(error);
        }
    };


    useEffect(() => {
        getBookingRequests();
    }, []);

    const filteredBookingRequests = bookingRequests.filter(booking => booking.email === user.email);
    return (
        <>
            <div className="available-requests">
                <h1>Booking Requests</h1>

                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredBookingRequests.map((booking) => (
                            <tr key={booking._id}>
                                <td>{booking.name}</td>
                                <td>{booking.email}</td>
                                <td>{booking.status}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
};


export default UserBookingRequests;
