import React, { useState, useEffect } from 'react';

const BookingRequest = () => {
    const [bookingData, setBookingData] = useState({ name: '', email: '', phone: '', car: '', pickupDate: '', returnDate: '' });
    const [carList, setCarList] = useState([]);

    // Simulating fetching car list from the backend
    useEffect(() => {
        const fetchCarList = async () => {
            // Replace the API endpoint with your actual endpoint to fetch car data
            const response = await fetch('api/cars');
            const data = await response.json();
            setCarList(data);
        };

        fetchCarList();
    }, []);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setBookingData({ ...bookingData, [name]: value });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Perform booking request submission logic here
        console.log(bookingData);
        // Reset form fields after submission
        setBookingData({ name: '', email: '', phone: '', car: '', pickupDate: '', returnDate: '' });
    };

    return (
        <section className="booking-request">
            <h2>Booking Request</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" name="name" value={bookingData.name} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" name="email" value={bookingData.email} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="phone">Phone:</label>
                    <input type="tel" id="phone" name="phone" value={bookingData.phone} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="car">Select Car:</label>
                    <select id="car" name="car" value={bookingData.car} onChange={handleInputChange} required>
                        <option value="">Select a car</option>
                        {carList.map((car) => (
                            <option key={car.id} value={car.id}>{car.name}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="pickupDate">Pickup Date:</label>
                    <input type="date" id="pickupDate" name="pickupDate" value={bookingData.pickupDate} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="returnDate">Return Date:</label>
                    <input type="date" id="returnDate" name="returnDate" value={bookingData.returnDate} onChange={handleInputChange} required />
                </div>
                <button type="submit">Submit</button>
            </form>
        </section>
    );
};

export default BookingRequest;
