import React, { useState } from 'react';

const BookingRequest = () => {
    const [bookingData, setBookingData] = useState({ name: '', email: '', phone: '', message: '' });

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setBookingData({ ...bookingData, [name]: value });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Perform booking request submission logic here
        console.log(bookingData);
        // Reset form fields after submission
        setBookingData({ name: '', email: '', phone: '', message: '' });
    };

    return (
        <section className="booking-request">
            <h2>Booking Request</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" name="name" value={bookingData.name} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" name="email" value={bookingData.email} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="phone">Phone:</label>
                    <input type="tel" id="phone" name="phone" value={bookingData.phone} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="message">Message:</label>
                    <textarea id="message" name="message" value={bookingData.message} onChange={handleInputChange} required></textarea>
                </div>
                <button type="submit">Submit</button>
            </form>
        </section>
    );
};

export default BookingRequest;
