import React, { useState, useEffect, useContext, useRef } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import { UserContext } from '../App';

const Rentacar = () => {
    const { state, dispatch } = useContext(UserContext);
    const history = useHistory();
    const [rentCarsData, setRentCarsData] = useState([]);
    const [rentDays, setRentDays] = useState('');
    const specsDivRef = useRef([]);
    const carDivRef = useRef([]);
    const formDivRef = useRef([]);

    const allRentCars = async () => {
        try {
            if (!state) {
                window.alert('Please sign in to see all available cars for rent!');
                history.push('/signin');
            }

            const res = await fetch('/getRentCarData', {
                method: 'GET',
            });

            const data = await res.json();
            setRentCarsData(data);

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        allRentCars();
    }, []);

    const showDetails = (e) => {
        let currentCar = e.target.id;
        if (specsDivRef.current[currentCar].style.display === 'none' && carDivRef.current[currentCar].style.display === 'block') {
            carDivRef.current[currentCar].style.display = 'none';
            specsDivRef.current[currentCar].style.display = 'block';
        } else {
            carDivRef.current[currentCar].style.display = 'block';
            specsDivRef.current[currentCar].style.display = 'none';
        }
    };

    const showCar = (e) => {
        let currentCar = e.target.id;
        if (specsDivRef.current[currentCar].style.display === 'block' && carDivRef.current[currentCar].style.display === 'none') {
            specsDivRef.current[currentCar].style.display = 'none';
            carDivRef.current[currentCar].style.display = 'block';
        } else {
            specsDivRef.current[currentCar].style.display = 'block';
            carDivRef.current[currentCar].style.display = 'none';
        }
    };

    const addToCart = (e) => {
        let currentCar = e.target.id;
        if (
            formDivRef.current[currentCar].style.display === 'none' &&
            specsDivRef.current[currentCar].style.display === 'none' &&
            carDivRef.current[currentCar].style.display === 'block'
        ) {
            carDivRef.current[currentCar].style.display = 'none';
            specsDivRef.current[currentCar].style.display = 'none';
            formDivRef.current[currentCar].style.display = 'block';
        } else {
            formDivRef.current[currentCar].style.display = 'none';
            specsDivRef.current[currentCar].style.display = 'none';
            carDivRef.current[currentCar].style.display = 'block';
        }
    };

    const showCarAgain = (e) => {
        let currentCar = e.target.id;
        if (
            formDivRef.current[currentCar].style.display === 'block' &&
            specsDivRef.current[currentCar].style.display === 'none' &&
            carDivRef.current[currentCar].style.display === 'none'
        ) {
            carDivRef.current[currentCar].style.display = 'block';
            specsDivRef.current[currentCar].style.display = 'none';
            formDivRef.current[currentCar].style.display = 'none';
        } else {
            formDivRef.current[currentCar].style.display = 'block';
            specsDivRef.current[currentCar].style.display = 'none';
            carDivRef.current[currentCar].style.display = 'none';
        }
    };

    const handleInputs = (e) => {
        let value = e.target.value;
        setRentDays(value);
    };

    const proceedToCart = async (e) => {
        e.preventDefault();
        let itemId = e.target.id;

        const res = await fetch('/addrentcartocart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                itemId,
                rentDays,
            }),
        });

        const data = await res.json();

        if (res.status === 500 || !data) {
            window.alert('Something went wrong');
        } else {
            window.alert('Item added. Please click on Go To cart to complete the purchase');
        }
    };

    const Loginbutton = () => {
        if (state) {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signout">
                            logout
                        </NavLink>
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button>
                        <NavLink className="btn" to="/signin">
                            login
                        </NavLink>
                    </button>
                </div>
            );
        }
    };

    const [searchText, setSearchText] = useState('');

    const searchTextBtn = async () => {
        const res = await fetch('/searchRentCar', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                searchText,
            }),
        });

        getSearchData();
    };

    const getSearchData = async () => {
        try {
            const res = await fetch('/rentcarsearchCategory', {
                method: 'GET',
            });

            const data = await res.json();

            setRentCarsData(data);

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <>
            <header className="header">
                <div id="menu-btn" className="fas fa-bars"></div>
                <NavLink className="logo" to="/">
                    <span>Car</span>Rent
                </NavLink>
                <nav className="navbar">
                    <NavLink className="nav-link" to="/">
                        Home
                    </NavLink>
                    <NavLink className="nav-link" to="/rentcarcart">
                        Go To Cart
                    </NavLink>
                    <input
                        type="text"
                        name="name"
                        placeholder="Search Car"
                        style={{ width: '30%', height: '8%' }}
                        value={searchText}
                        onChange={(e) => setSearchText(e.target.value)}
                        className="btn"
                    />
                    <button type="submit" onClick={searchTextBtn} className="btn">
                        <i className="fa fa-search"></i>
                    </button>
                </nav>
                <div id="login-btn">
                    <Loginbutton />
                </div>
            </header>

            <div className="rentcarcard">
                {rentCarsData.map((rentCarData, index) => (
                    <React.Fragment key={rentCarData._id}>
                        <div className="cardivRentcar" ref={(el) => (carDivRef.current[index] = el)}>
                            <img src={rentCarData.filePath} alt="" style={{ width: '80%', height: '70%' }} />
                            <h4>{rentCarData.brand}</h4>
                            <p>{rentCarData.model}</p>
                            <div style={{ display: 'flex', gap: '15px' }}>
                                <button className="cardbtn" id={index} onClick={showDetails}>
                                    Details
                                </button>
                                <br />
                                <button className="cardbtn" id={index} onClick={addToCart}>
                                    Add To Cart
                                </button>
                                <br />
                            </div>
                        </div>

                        <div className="specsDivRentcar" ref={(el) => (specsDivRef.current[index] = el)}>
                            <p>Brand : {rentCarData.brand}</p>
                            <p>Model : {rentCarData.model}</p>
                            <p>Year : {rentCarData.year}</p>
                            <p>Color : {rentCarData.color}</p>
                            <p>Seats : {rentCarData.seats}</p>
                            <p>Rent Per Day : {rentCarData.rent} DA</p>
                            <p style={{ color: 'red' }}>Availability : {rentCarData.availability + ' days'}</p>
                            <div style={{ display: 'flex', gap: '15px' }}>
                                <button className="cardbtn">
                                    <NavLink className="nav-link" to={{ pathname: '/rentcarreviews', state: { id: rentCarData._id } }}>
                                        Car Reviews
                                    </NavLink>
                                </button>
                                <button className="cardbtn" id={index} onClick={showCar}>
                                    show car
                                </button>
                            </div>
                        </div>

                        <div className="formDivRentcar" ref={(el) => (formDivRef.current[index] = el)}>
                            <form method="POST">
                                <h3>Before click on proceed please enter for how many days do you want to rent the car</h3>
                                <br />
                                <label htmlFor="lname">Rent Days: </label>
                                <br />
                                <input type="text" className="cardbtn" name="rentfordays" value={rentDays} onChange={handleInputs} placeholder="Enter rent days" />
                                <br />
                                <input type="submit" className="cardbtn" value="Proceed" id={rentCarData._id} onClick={proceedToCart} />
                            </form>
                            <button className="cardbtn" id={index} onClick={showCarAgain}>
                                show car
                            </button>
                        </div>
                    </React.Fragment>
                ))}
            </div>
        </>
    );
};

export default Rentacar;
