import React, { useState } from 'react';
import Swal from 'sweetalert2';

const BookingRequest = () => {
    const [bookingData, setBookingData] = useState({
        name: '',
        email: '',
        phone: '',
        pickupDate: '',
        returnDate: '',
        selectedCar: ''
    });

    const handleInputs = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setBookingData({ ...bookingData, [name]: value });
    }

    const sendRequest = async (e) => {
        e.preventDefault();

        const { name, email, phone, pickupDate, returnDate, selectedCar } = bookingData;

        if (!name || !email || !phone || !pickupDate || !returnDate || !selectedCar) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Request',
                text: 'Please fill in all the fields',
                showConfirmButton: true,
            });
            return;
        }

        try {
            const res = await fetch('/saveBooking', {
                method: 'POST',
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({
                    name,
                    email,
                    phone,
                    pickupDate,
                    returnDate,
                    selectedCar
                })
            });

            const data = await res.json();

            if (!data) {
                console.log("Request not sent");
            } else {
                Swal.fire({
                    icon: 'success',
                    title: 'Your request has been saved',
                    showConfirmButton: false,
                    timer: 1500
                });
                setBookingData({
                    name: '',
                    email: '',
                    phone: '',
                    pickupDate: '',
                    returnDate: '',
                    selectedCar: ''
                });
            }
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <div className="booking-request">
            <h1>Booking Request</h1>
            <form>
                <input type="text" name="name" value={bookingData.name} onChange={handleInputs} placeholder="Your Name" required />
                <input type="email" name="email" value={bookingData.email} onChange={handleInputs} placeholder="Your Email" required />
                <input type="tel" name="phone" value={bookingData.phone} onChange={handleInputs} placeholder="Your Phone" required />
                <input type="date" name="pickupDate" value={bookingData.pickupDate} onChange={handleInputs} placeholder="Pickup Date" required />
                <input type="date" name="returnDate" value={bookingData.returnDate} onChange={handleInputs} placeholder="Return Date" required />
                <select name="selectedCar" value={bookingData.selectedCar} onChange={handleInputs} required>
                    <option value="">Select a Car</option>
                    <option value="Car 1">Car 1</option>
                    <option value="Car 2">Car 2</option>
                    <option value="Car 3">Car 3</option>
                </select>
                <button type="submit" onClick={sendRequest}>Send Request</button>
            </form>
        </div>
    );
}

export default BookingRequest;
