import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from "react-router-dom";
import Swal from 'sweetalert2';

import { UserContext } from "../App";

const Rentacar = () => {
    const { state, dispatch } = useContext(UserContext);
    const history = useHistory();
    const [rentCarsData, setRentCarsData] = useState([]);

    const allRentCars = async () => {
        try {
            if (!state) {
                Swal.fire({
                    title: 'Please sign in to rent a car!',
                    icon: 'warning',
                    showCancelButton: true,
                    confirmButtonText: 'OK'
                }).then((result) => {
                    if (result.isConfirmed) {
                        history.push('/signin');
                    } else {
                        history.push('/');
                    }
                });
            }

            const res = await fetch('/getRentCarData', {
                method: 'GET',
            });

            const data = await res.json();
            setRentCarsData(data);

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        allRentCars();
    }, []);

    const specsDiv = document.getElementsByClassName("specsDivRentcar");
    const carDiv = document.getElementsByClassName("cardivRentcar");
    const formDiv = document.getElementsByClassName("formDivRentcar");

    const showDetails = (e) => {
        let currentCar = e.target.id;
        if (
            specsDiv[currentCar].style.display === "none" &&
            carDiv[currentCar].style.display === "block"
        ) {
            carDiv[currentCar].style.display = "none";
            specsDiv[currentCar].style.display = "block";
        } else {
            carDiv[currentCar].style.display = "block";
            specsDiv[currentCar].style.display = "none";
        }
    };

    const showCar = (e) => {
        let currentCar = e.target.id;
        if (
            specsDiv[currentCar].style.display === "block" &&
            carDiv[currentCar].style.display === "none"
        ) {
            specsDiv[currentCar].style.display = "none";
            carDiv[currentCar].style.display = "block";
        } else {
            specsDiv[currentCar].style.display = "block";
            carDiv[currentCar].style.display = "none";
        }
    };

    const [rentDays, setRentDays] = useState('');
    const handleInputs = (e) => {
        let value = e.target.value;
        setRentDays(value);
    };

    const addToCart = (e) => {
        let currentCar = e.target.id;
        if (
            formDiv[currentCar].style.display === "none" &&
            specsDiv[currentCar].style.display === "none" &&
            carDiv[currentCar].style.display === "block"
        ) {
            carDiv[currentCar].style.display = "none";
            specsDiv[currentCar].style.display = "none";
            formDiv[currentCar].style.display = "block";
        } else {
            formDiv[currentCar].style.display = "none";
            specsDiv[currentCar].style.display = "none";
            carDiv[currentCar].style.display = "block";
        }
    };

    const showCarAgain = (e) => {
        let currentCar = e.target.id;
        if (
            formDiv[currentCar].style.display === "block" &&
            specsDiv[currentCar].style.display === "none" &&
            carDiv[currentCar].style.display === "none"
        ) {
            formDiv[currentCar].style.display = "none";
            specsDiv[currentCar].style.display = "none";
            carDiv[currentCar].style.display = "block";
        } else {
            formDiv[currentCar].style.display = "none";
            specsDiv[currentCar].style.display = "none";
            carDiv[currentCar].style.display = "block";
        }
    };

    const proceedToCart = async (e) => {
        let currentCar = e.target.id;
        if (!rentDays) {
            Swal.fire({
                title: "Please enter the number of rent days!",
                icon: "warning",
                confirmButtonText: "OK",
            });
        } else {
            try {
                const res = await fetch("/addtocart", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        rentDays: rentDays,
                        rentCarId: rentCarsData[currentCar]._id,
                    }),
                });

                const data = await res.json();

                if (res.status === 201) {
                    Swal.fire({
                        title: data.message,
                        icon: "success",
                        confirmButtonText: "OK",
                    }).then((result) => {
                        if (result.isConfirmed) {
                            history.push("/cart");
                        }
                    });
                } else {
                    Swal.fire({
                        title: data.message,
                        icon: "error",
                        confirmButtonText: "OK",
                    });
                }
            } catch (error) {
                console.log(error);
            }
        }
    };

    return (
        <>
            <div className="rentcarDiv">
                <div className="flexBoxRentcar">
                    {rentCarsData.map((car, index) => {
                        return (
                            <div key={car._id} className="rentcarcard">
                                <div className="cardivRentcar">
                                    <h1>{car.carname}</h1>
                                    <img src={car.image} alt="car" />
                                    <button
                                        id={index}
                                        className="btnRentcar"
                                        onClick={showDetails}
                                    >
                                        Show Details
                                    </button>
                                    <button
                                        id={index}
                                        className="btnRentcar"
                                        onClick={addToCart}
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                                <div className="specsDivRentcar">
                                    <h1>{car.carname}</h1>
                                    <p>Brand: {car.brand}</p>
                                    <p>Color: {car.color}</p>
                                    <p>Price: {car.price}</p>
                                    <button
                                        id={index}
                                        className="btnRentcar"
                                        onClick={showCar}
                                    >
                                        Show Car
                                    </button>
                                    <button
                                        id={index}
                                        className="btnRentcar"
                                        onClick={addToCart}
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                                <div className="formDivRentcar">
                                    <h1>{car.carname}</h1>
                                    <p>Price: {car.price}</p>
                                    <input
                                        type="number"
                                        placeholder="Enter number of days"
                                        autoComplete="off"
                                        value={rentDays}
                                        onChange={handleInputs}
                                    />
                                    <button
                                        id={index}
                                        className="btnRentcar"
                                        onClick={proceedToCart}
                                    >
                                        Proceed to Cart
                                    </button>
                                    <button
                                        id={index}
                                        className="btnRentcar"
                                        onClick={showCarAgain}
                                    >
                                        Show Car
                                    </button>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </div>
        </>
    );
};

export default Rentacar;
