import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';



//make a booking request
const BookingRequest = () => {
    const [userData, setUserData] = useState({ name: "", email: "", phone: "" });

    const [bookingData, setBookingData] = useState({
        name: '',
        email: '',
        phone: '',
        pickupDate: '',
        returnDate: '',
        car: ''
    });
    const [carList, setCarList] = useState([]);
    const { state } = useContext(UserContext);
    const history = useHistory();
    useEffect(() => {
        if (!state) {
            Swal.fire({
                title: 'Please sign in to make a booking request!',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'OK',
            }).then((result) => {
                if (result.isConfirmed) {
                    history.push('/signin');
                } else {
                    history.push('/');
                }
            });
        } else {
            // Fetch user data from the server and update the state
            const fetchUserData = async () => {
                try {
                    const res = await fetch('/getdata', {
                        method: 'GET',
                        headers: {
                            'Content-Type': 'application/json'
                        },
                    });

                    const data = await res.json();

                    setUserData({ ...userData, name: data.name, email: data.email, phone: data.phone });

                    if (!res.ok) {
                        throw new Error(res.error);
                    }
                } catch (error) {
                    console.log(error);
                }
            };

            fetchUserData();
        }
    }, [state, history]);
    const handleInputs = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setUserData({ ...userData, [name]: value });
    };
    useEffect(() => {
        const fetchCarList = async () => {
            try {
                const response = await fetch('/getAvailableRentCars');
                const data = await response.json();
                const carNames = data.map((car) => `${car.brand} ${car.model} ${car.year}`);
                setCarList(carNames);
            } catch (error) {
                console.error('Error fetching car list:', error);
            }
        };

        fetchCarList();
    }, []);

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setBookingData({ ...bookingData, [name]: value });
    };

    const sendRequest = async (e) => {
        e.preventDefault();

        const { name, email, phone, pickupDate, returnDate, car } = bookingData;

        if (!name || !email || !phone || !pickupDate || !returnDate || !car) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Request',
                text: 'Please fill in all the fields',
                showConfirmButton: true,
            });
            return;
        }

        try {
            const res = await fetch('/saveBooking', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name,
                    email,
                    phone,
                    pickupDate,
                    returnDate,
                    car
                })
            });

            const data = await res.json();

            if (!data) {
                console.log('Request not sent');
            } else {
                Swal.fire({
                    icon: 'success',
                    title: 'Your request has been saved',
                    showConfirmButton: false,
                    timer: 1500
                });
                setBookingData({
                    name: '',
                    email: '',
                    phone: '',
                    pickupDate: '',
                    returnDate: '',
                    car: ''
                });
            }
        } catch (error) {
            console.log(error);
        }
    };
    const LoginButton = () => {
        if (state) {
            return (
                <div>
                    <button><NavLink className="btn" to="/signout">logout</NavLink></button>
