import React, { useState, useEffect, useContext } from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';
import { UserContext } from '../App';

const BookingList = () => {
    const { state } = useContext(UserContext);
    const [bookings, setBookings] = useState([]);
    const history = useHistory();
    useEffect(() => {
        if (!state) {
            Swal.fire({
                title: 'Please sign in to track your booking request!',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'OK',
            }).then((result) => {
                if (result.isConfirmed) {
                    history.push('/signin');
                } else {
                    history.push('/');
                }
            });
        }
    }, [state, history]);
    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const response = await fetch('/getStatus', {
                    headers: {
                        Authorization: `Bearer ${state.token}` // Add the user's authentication token
                    }
                });
                const data = await response.json();
                setBookings(data.bookings);
            } catch (error) {
                console.error('Error fetching bookings:', error);
            }
        };

        fetchBookings();
    }, [state]);
    const deleteBooking = async (bookingId) => {
        try {
            await fetch(`/deletemyBooking/${bookingId}`, {
                method: 'DELETE',
            });
            setBookings((prevBookings) =>
                prevBookings.filter((booking) => booking._id !== bookingId)
            );
            Swal.fire('Success', 'Booking deleted successfully', 'success');
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <>
            <header className="header">
                <div id="menu-btn" className="fas fa-bars"></div>
                <NavLink className="logo" to="/">
                    <span>Krily</span>Car{' '}
                </NavLink>
                <nav className="navbar">
                    <NavLink to="/">Home</NavLink>
                    <NavLink to="/rentcar">Rent A Car</NavLink>
                </nav>
                <div id="login-btn">
                    <Loginbutton />
                </div>
            </header>
            <div>
                <h1>Booking Requests</h1>
                {bookings.length > 0 ? (
                    <ul>
                        {bookings.map((booking, index) => (
                            <li key={index}>
                                <p>User: {booking.user}</p>
                                <p>Email: {booking.email}</p>
                                <p>Phone: {booking.phone}</p>
                                <p>Pickup Date: {booking.pickupDate}</p>
                                <p>Return Date: {booking.returnDate}</p>
                                <p>Car: {booking.car}</p>
                                <p>Status: {booking.status}</p>
                                <button
                                    className="delete-button"
                                    onClick={() => deleteBooking(booking._id)}
                                >
                                    Delete
                                </button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No bookings found.</p>
                )}
            </div>
        </>
    );
};

export default BookingList;
