import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { Bar } from 'react-chartjs-2';

import { AdminContext } from '../../App';

const Rentcarreports = () => {
    const { adminState, dispatchadmin } = useContext(AdminContext);

    const [incomeByDate, setIncomeByDate] = useState({});

    const getRentCarIncome = async () => {
        try {
            const res = await fetch('/getrentcarincome', {
                method: 'GET'
            });

            const data = await res.json();

            setIncomeByDate(data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getRentCarIncome();
    }, []);

    const generateChartData = () => {
        const labels = Object.keys(incomeByDate);
        const totalIncomeData = Object.values(incomeByDate).map(income => income.totalIncome);
        const soldItemsCountData = Object.values(incomeByDate).map(income => income.count);

        return {
            labels,
            datasets: [
                {
                    label: 'Total Income',
                    data: totalIncomeData,
                    backgroundColor: 'rgba(75, 192, 192, 0.2)',
                    borderColor: 'rgba(75, 192, 192, 1)',
                    borderWidth: 1
                },
                {
                    label: 'Sold Items Count',
                    data: soldItemsCountData,
                    backgroundColor: 'rgba(54, 162, 235, 0.2)',
                    borderColor: 'rgba(54, 162, 235, 1)',
                    borderWidth: 1
                }
            ]
        };
    };

    const Loginbutton = () => {
        // Login button logic
    };

    return (
        <>
            <div className="sidebar">
                {/* Sidebar content */}
            </div>

            <section className="home-section">
                {/* Navigation and profile details */}
                <div className="salecartableDiv">
                    <h1 className="heading">
                        <span>Rented Cars Income Report</span>
                    </h1>

                    <div className="chart-container">
                        <Bar data={generateChartData()} options={{ responsive: true }} />
                    </div>
                </div>
            </section>
        </>
    );
};

export default Rentcarreports;
